vite.config.ts.content:
import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on port 5000
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = 5000;
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { generateContentRecommendations } from "./gemini";
import { 
  hashPassword, 
  verifyPassword, 
  generateToken, 
  authenticateAdmin, 
  requireRole,
  type AuthenticatedRequest 
} from "./auth";
import {
  insertContactSubmissionSchema,
  insertEmailCampaignSchema,
  insertContentBlockSchema,
  insertAdminUserSchema,
  insertPageSchema,
  insertPageComponentSchema,
  insertPageTemplateSchema,
  insertDashboardWidgetSchema,
  insertUserDashboardSchema,
  insertDashboardWidgetInstanceSchema
} from "@shared/schema";
import { generatePageComponent, generateFullPage } from "./pageBuilder";

export async function registerRoutes(app: Express): Promise<Server> {
  // Content recommendations endpoint
  app.post("/api/recommendations", async (req, res) => {
    try {
      console.log("Recommendations API called");
      const { currentPath, pageContent } = req.body;

      if (!currentPath || !pageContent) {
        console.log("Missing fields:", { currentPath: !!currentPath, pageContent: !!pageContent });
        return res.status(400).json({ error: "Missing required fields" });
      }

      console.log("Generating recommendations for:", currentPath);
      const recommendations = await generateContentRecommendations(pageContent, currentPath);
      console.log("Generated recommendations:", recommendations.length);
      res.json(recommendations);
    } catch (error) {
      console.error("Error generating recommendations:", error);
      // Return fallback recommendations for now
      const fallbackRecommendations = [
        {
          title: "Cloud Transformation",
          type: "service",
          description: "Modernize your infrastructure with enterprise cloud solutions",
          url: "/services/cloud",
          relevanceScore: 0.8
        },
        {
          title: "Financial Services",
          type: "industry", 
          description: "Specialized solutions for banking and financial institutions",
          url: "/industries/financial-services",
          relevanceScore: 0.75
        },
        {
          title: "AI & Machine Learning",
          type: "service",
          description: "Intelligent automation and predictive analytics solutions", 
          url: "/services/ai-ml",
          relevanceScore: 0.85
        }
      ];
      res.json(fallbackRecommendations);
    }
  });

  // Analytics tracking endpoints
  app.post("/api/analytics/pageview", async (req, res) => {
    try {
      const { path, userAgent, referrer, sessionId } = req.body;
      const ipAddress = req.ip || req.connection.remoteAddress;

      await storage.createPageView({
        path,
        userAgent,
        ipAddress,
        referrer,
        sessionId,
        device: userAgent?.includes('Mobile') ? 'mobile' : 'desktop'
      });

      res.json({ success: true });
    } catch (error) {
      console.error("Error tracking page view:", error);
      res.status(500).json({ error: "Failed to track page view" });
    }
  });

  app.post("/api/analytics/ai-interaction", async (req, res) => {
    try {
      const { sessionId, currentPage, recommendationsShown, clickedRecommendation } = req.body;

      await storage.createAiInteraction({
        sessionId,
        currentPage,
        recommendationsShown,
        clickedRecommendation
      });

      res.json({ success: true });
    } catch (error) {
      console.error("Error tracking AI interaction:", error);
      res.status(500).json({ error: "Failed to track AI interaction" });
    }
  });

  // Contact form submission
  app.post("/api/contact", async (req, res) => {
    try {
      const validatedData = insertContactSubmissionSchema.parse(req.body);
      const submission = await storage.createContactSubmission(validatedData);
      res.json({ success: true, id: submission.id });
    } catch (error) {
      console.error("Error submitting contact form:", error);
      res.status(400).json({ error: "Invalid form data" });
    }
  });

  // Admin Authentication Routes
  app.post("/api/admin/login", async (req, res) => {
    try {
      const { email, password } = req.body;

      if (!email || !password) {
        return res.status(400).json({ error: "Email and password required" });
      }

      const admin = await storage.getAdminUserByEmail(email);
      if (!admin || !admin.isActive) {
        return res.status(401).json({ error: "Invalid credentials" });
      }

      const isValidPassword = await verifyPassword(password, admin.passwordHash);
      if (!isValidPassword) {
        return res.status(401).json({ error: "Invalid credentials" });
      }

      // Update last login
      await storage.updateAdminUserLastLogin(admin.id);

      const token = generateToken({
        id: admin.id,
        email: admin.email,
        name: admin.name,
        role: admin.role
      });

      res.json({
        token,
        user: {
          id: admin.id,
          email: admin.email,
          name: admin.name,
          role: admin.role
        }
      });
    } catch (error) {
      console.error("Login error:", error);
      res.status(500).json({ error: "Login failed" });
    }
  });

  app.post("/api/admin/register", async (req, res) => {
    try {
      const { email, password, name, role = "admin" } = req.body;

      if (!email || !password || !name) {
        return res.status(400).json({ error: "Email, password, and name required" });
      }

      // Check if admin already exists
      const existingAdmin = await storage.getAdminUserByEmail(email);
      if (existingAdmin) {
        return res.status(400).json({ error: "Admin user already exists" });
      }

      const passwordHash = await hashPassword(password);
      const admin = await storage.createAdminUser({
        email,
        passwordHash,
        name,
        role
      });

      res.json({
        success: true,
        user: {
          id: admin.id,
          email: admin.email,
          name: admin.name,
          role: admin.role
        }
      });
    } catch (error) {
      console.error("Registration error:", error);
      res.status(500).json({ error: "Registration failed" });
    }
  });

  // Protected Admin Routes
  app.use("/api/admin/dashboard", authenticateAdmin);

  // Dashboard analytics
  app.get("/api/admin/dashboard/stats", async (req: AuthenticatedRequest, res) => {
    try {
      const stats = await storage.getDashboardStats();
      const aiStats = await storage.getAiInteractionStats();
      const topPages = await storage.getTopPages(10);
      const deviceStats = await storage.getDeviceStats();
      const countryStats = await storage.getCountryStats();

      res.json({
        ...stats,
        ...aiStats,
        topPages,
        deviceStats,
        countryStats
      });
    } catch (error) {
      console.error("Error fetching dashboard stats:", error);
      res.status(500).json({ error: "Failed to fetch stats" });
    }
  });

  // Contact submissions management
  app.get("/api/admin/dashboard/contacts", async (req: AuthenticatedRequest, res) => {
    try {
      const page = parseInt(req.query.page as string) || 1;
      const limit = parseInt(req.query.limit as string) || 50;
      const offset = (page - 1) * limit;

      const contacts = await storage.getContactSubmissions(limit, offset);
      const total = await storage.getContactSubmissionsCount();

      res.json({
        contacts,
        pagination: {
          page,
          limit,
          total,
          pages: Math.ceil(total / limit)
        }
      });
    } catch (error) {
      console.error("Error fetching contacts:", error);
      res.status(500).json({ error: "Failed to fetch contacts" });
    }
  });

  app.patch("/api/admin/dashboard/contacts/:id", async (req: AuthenticatedRequest, res) => {
    try {
      const id = parseInt(req.params.id);
      const { status } = req.body;

      if (!['new', 'contacted', 'qualified', 'closed'].includes(status)) {
        return res.status(400).json({ error: "Invalid status" });
      }

      const updated = await storage.updateContactSubmissionStatus(id, status);
      res.json(updated);
    } catch (error) {
      console.error("Error updating contact:", error);
      res.status(500).json({ error: "Failed to update contact" });
    }
  });

  // Email campaigns
  app.get("/api/admin/dashboard/campaigns", async (req: AuthenticatedRequest, res) => {
    try {
      const campaigns = await storage.getEmailCampaigns();
      res.json(campaigns);
    } catch (error) {
      console.error("Error fetching campaigns:", error);
      res.status(500).json({ error: "Failed to fetch campaigns" });
    }
  });

  app.post("/api/admin/dashboard/campaigns", async (req: AuthenticatedRequest, res) => {
    try {
      const validatedData = insertEmailCampaignSchema.parse(req.body);
      const campaign = await storage.createEmailCampaign(validatedData);
      res.json(campaign);
    } catch (error) {
      console.error("Error creating campaign:", error);
      res.status(400).json({ error: "Invalid campaign data" });
    }
  });

  app.patch("/api/admin/dashboard/campaigns/:id", async (req: AuthenticatedRequest, res) => {
    try {
      const id = parseInt(req.params.id);
      const updates = req.body;
      const campaign = await storage.updateEmailCampaign(id, updates);
      res.json(campaign);
    } catch (error) {
      console.error("Error updating campaign:", error);
      res.status(500).json({ error: "Failed to update campaign" });
    }
  });

  app.delete("/api/admin/dashboard/campaigns/:id", async (req: AuthenticatedRequest, res) => {
    try {
      const id = parseInt(req.params.id);
      await storage.deleteEmailCampaign(id);
      res.json({ success: true });
    } catch (error) {
      console.error("Error deleting campaign:", error);
      res.status(500).json({ error: "Failed to delete campaign" });
    }
  });

  // Content management
  app.get("/api/admin/dashboard/content", async (req: AuthenticatedRequest, res) => {
    try {
      const page = req.query.page as string;
      const content = await storage.getContentBlocks(page);
      res.json(content);
    } catch (error) {
      console.error("Error fetching content:", error);
      res.status(500).json({ error: "Failed to fetch content" });
    }
  });

  app.post("/api/admin/dashboard/content", async (req: AuthenticatedRequest, res) => {
    try {
      const validatedData = insertContentBlockSchema.parse(req.body);
      const content = await storage.createContentBlock(validatedData);
      res.json(content);
    } catch (error) {
      console.error("Error creating content:", error);
      res.status(400).json({ error: "Invalid content data" });
    }
  });

  app.patch("/api/admin/dashboard/content/:id", async (req: AuthenticatedRequest, res) => {
    try {
      const id = parseInt(req.params.id);
      const updates = req.body;
      const content = await storage.updateContentBlock(id, updates);
      res.json(content);
    } catch (error) {
      console.error("Error updating content:", error);
      res.status(500).json({ error: "Failed to update content" });
    }
  });

  // System logs
  app.get("/api/admin/dashboard/logs", requireRole('super_admin'), async (req: AuthenticatedRequest, res) => {
    try {
      const level = req.query.level as string;
      const limit = parseInt(req.query.limit as string) || 100;
      const logs = await storage.getSystemLogs(level, limit);
      res.json(logs);
    } catch (error) {
      console.error("Error fetching logs:", error);
      res.status(500).json({ error: "Failed to fetch logs" });
    }
  });

  // Contact form submission
  app.post('/api/contact', async (req, res) => {
    try {
      const submission = await storage.createContactSubmission(req.body);
      res.json(submission);
    } catch (error) {
      console.error('Error creating contact submission:', error);
      res.status(500).json({ message: 'Failed to submit contact form' });
    }
  });

  // User Management endpoints
  app.get('/api/admin/users', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const users = await storage.getAllAdminUsers();
      res.json(users);
    } catch (error) {
      console.error('Error fetching users:', error);
      res.status(500).json({ message: 'Failed to fetch users' });
    }
  });

  app.post('/api/admin/users', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const { email, name, role, password } = req.body;

      const existingUser = await storage.getAdminUserByEmail(email);
      if (existingUser) {
        return res.status(400).json({ message: 'User with this email already exists' });
      }

      const hashedPassword = await hashPassword(password);
      const user = await storage.createAdminUser({
        email,
        name,
        role,
        passwordHash: hashedPassword,
      });

      const { passwordHash, ...userResponse } = user;
      res.json(userResponse);
    } catch (error) {
      console.error('Error creating user:', error);
      res.status(500).json({ message: 'Failed to create user' });
    }
  });

  app.patch('/api/admin/users/:id', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const userId = parseInt(req.params.id);
      const updates = req.body;

      const updatedUser = await storage.updateAdminUser(userId, updates);
      const { passwordHash, ...userResponse } = updatedUser;
      res.json(userResponse);
    } catch (error) {
      console.error('Error updating user:', error);
      res.status(500).json({ message: 'Failed to update user' });
    }
  });

  app.delete('/api/admin/users/:id', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const userId = parseInt(req.params.id);
      await storage.deleteAdminUser(userId);
      res.json({ message: 'User deleted successfully' });
    } catch (error) {
      console.error('Error deleting user:', error);
      res.status(500).json({ message: 'Failed to delete user' });
    }
  });

  // Profile Settings endpoints
  app.patch('/api/admin/profile', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const { name } = req.body;
      const userId = req.adminUser!.id;

      const updatedUser = await storage.updateAdminUser(userId, { name });
      const { passwordHash, ...userResponse } = updatedUser;
      res.json(userResponse);
    } catch (error) {
      console.error('Error updating profile:', error);
      res.status(500).json({ message: 'Failed to update profile' });
    }
  });

  app.post('/api/admin/change-password', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const { currentPassword, newPassword } = req.body;
      const userId = req.adminUser!.id;

      const user = await storage.getAdminUser(userId);
      if (!user) {
        return res.status(404).json({ message: 'User not found' });
      }

      const isValidPassword = await verifyPassword(currentPassword, user.passwordHash);
      if (!isValidPassword) {
        return res.status(400).json({ message: 'Current password is incorrect' });
      }

      const hashedNewPassword = await hashPassword(newPassword);
      await storage.updateAdminUser(userId, { passwordHash: hashedNewPassword });

      res.json({ message: 'Password changed successfully' });
    } catch (error) {
      console.error('Error changing password:', error);
      res.status(500).json({ message: 'Failed to change password' });
    }
  });

  // Integrations endpoints
  app.get('/api/admin/integrations', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const integrations = await storage.getIntegrations();
      res.json(integrations);
    } catch (error) {
      console.error('Error fetching integrations:', error);
      res.status(500).json({ message: 'Failed to fetch integrations' });
    }
  });

  app.post('/api/admin/integrations', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const integration = await storage.createIntegration(req.body);
      res.json(integration);
    } catch (error) {
      console.error('Error creating integration:', error);
      res.status(500).json({ message: 'Failed to create integration' });
    }
  });

  app.patch('/api/admin/integrations/:id', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const integrationId = parseInt(req.params.id);
      const updates = req.body;

      const updatedIntegration = await storage.updateIntegration(integrationId, updates);
      res.json(updatedIntegration);
    } catch (error) {
      console.error('Error updating integration:', error);
      res.status(500).json({ message: 'Failed to update integration' });
    }
  });

  app.delete('/api/admin/integrations/:id', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const integrationId = parseInt(req.params.id);
      await storage.deleteIntegration(integrationId);
      res.json({ message: 'Integration deleted successfully' });
    } catch (error) {
      console.error('Error deleting integration:', error);
      res.status(500).json({ message: 'Failed to delete integration' });
    }
  });

  app.post('/api/admin/integrations/:id/test', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const integrationId = parseInt(req.params.id);
      const integration = await storage.getIntegrationById(integrationId);

      if (!integration) {
        return res.status(404).json({ message: 'Integration not found' });
      }

      // Basic test logic - in a real implementation, you'd test the actual API
      if (integration.type === 'zapier' && integration.webhookUrl) {
        res.json({ message: 'Zapier webhook URL is valid and reachable' });
      } else if (integration.type === 'brevo' && integration.apiKey) {
        res.json({ message: 'Brevo API key format is valid' });
      } else {
        res.json({ message: 'Integration configuration appears valid' });
      }
    } catch (error) {
      console.error('Error testing integration:', error);
      res.status(500).json({ message: 'Failed to test integration' });
    }
  });

  // ========== PAGE BUILDER API ROUTES ==========

  // Get all pages
  app.get('/api/admin/pages', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const { status } = req.query;
      const pages = await storage.getPages(status as string);
      res.json(pages);
    } catch (error) {
      console.error('Error fetching pages:', error);
      res.status(500).json({ message: 'Failed to fetch pages' });
    }
  });

  // Create new page
  app.post('/api/admin/pages', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const pageData = insertPageSchema.parse(req.body);
      const newPage = await storage.createPage(pageData);

      // Log user activity
      await storage.createUserActivity({
        adminUserId: req.adminUser?.id,
        activity: 'Created page',
        details: { pageId: newPage.id, title: newPage.title },
        ipAddress: req.ip,
        userAgent: req.get('User-Agent')
      });

      res.status(201).json(newPage);
    } catch (error) {
      console.error('Error creating page:', error);
      res.status(500).json({ message: 'Failed to create page' });
    }
  });

  // Get page by ID
  app.get('/api/admin/pages/:id', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const pageId = parseInt(req.params.id);
      const page = await storage.getPageById(pageId);

      if (!page) {
        return res.status(404).json({ message: 'Page not found' });
      }

      res.json(page);
    } catch (error) {
      console.error('Error fetching page:', error);
      res.status(500).json({ message: 'Failed to fetch page' });
    }
  });

  // Update page
  app.put('/api/admin/pages/:id', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const pageId = parseInt(req.params.id);
      const updates = req.body;
      const updatedPage = await storage.updatePage(pageId, { ...updates, updatedBy: req.adminUser?.id });

      // Log user activity
      await storage.createUserActivity({
        adminUserId: req.adminUser?.id,
        activity: 'Updated page',
        details: { pageId, changes: Object.keys(updates) },
        ipAddress: req.ip,
        userAgent: req.get('User-Agent')
      });

      res.json(updatedPage);
    } catch (error) {
      console.error('Error updating page:', error);
      res.status(500).json({ message: 'Failed to update page' });
    }
  });

  // Delete page
  app.delete('/api/admin/pages/:id', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const pageId = parseInt(req.params.id);
      await storage.deletePage(pageId);

      // Log user activity
      await storage.createUserActivity({
        adminUserId: req.adminUser?.id,
        activity: 'Deleted page',
        details: { pageId },
        ipAddress: req.ip,
        userAgent: req.get('User-Agent')
      });

      res.json({ message: 'Page deleted successfully' });
    } catch (error) {
      console.error('Error deleting page:', error);
      res.status(500).json({ message: 'Failed to delete page' });
    }
  });

  // Get page components
  app.get('/api/admin/pages/:id/components', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const pageId = parseInt(req.params.id);
      const components = await storage.getPageComponents(pageId);
      res.json(components);
    } catch (error) {
      console.error('Error fetching page components:', error);
      res.status(500).json({ message: 'Failed to fetch page components' });
    }
  });

  // Create page component
  app.post('/api/admin/pages/:id/components', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const pageId = parseInt(req.params.id);
      const componentData = insertPageComponentSchema.parse({ ...req.body, pageId });
      const newComponent = await storage.createPageComponent(componentData);

      // Log user activity
      await storage.createUserActivity({
        adminUserId: req.adminUser?.id,
        activity: 'Added page component',
        details: { pageId, componentType: newComponent.type, componentId: newComponent.id },
        ipAddress: req.ip,
        userAgent: req.get('User-Agent')
      });

      res.status(201).json(newComponent);
    } catch (error) {
      console.error('Error creating page component:', error);
      res.status(500).json({ message: 'Failed to create page component' });
    }
  });

  // Update page component
  app.put('/api/admin/components/:id', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const componentId = parseInt(req.params.id);
      const updates = req.body;
      const updatedComponent = await storage.updatePageComponent(componentId, updates);

      // Log user activity
      await storage.createUserActivity({
        adminUserId: req.adminUser?.id,
        activity: 'Updated page component',
        details: { componentId, changes: Object.keys(updates) },
        ipAddress: req.ip,
        userAgent: req.get('User-Agent')
      });

      res.json(updatedComponent);
    } catch (error) {
      console.error('Error updating page component:', error);
      res.status(500).json({ message: 'Failed to update page component' });
    }
  });

  // Delete page component
  app.delete('/api/admin/components/:id', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const componentId = parseInt(req.params.id);
      await storage.deletePageComponent(componentId);

      // Log user activity
      await storage.createUserActivity({
        adminUserId: req.adminUser?.id,
        activity: 'Deleted page component',
        details: { componentId },
        ipAddress: req.ip,
        userAgent: req.get('User-Agent')
      });

      res.json({ message: 'Page component deleted successfully' });
    } catch (error) {
      console.error('Error deleting page component:', error);
      res.status(500).json({ message: 'Failed to delete page component' });
    }
  });

  // Reorder page components
  app.put('/api/admin/pages/:id/components/reorder', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const pageId = parseInt(req.params.id);
      const { componentIds } = req.body;
      await storage.reorderPageComponents(pageId, componentIds);

      // Log user activity
      await storage.createUserActivity({
        adminUserId: req.adminUser?.id,
        activity: 'Reordered page components',
        details: { pageId, componentOrder: componentIds },
        ipAddress: req.ip,
        userAgent: req.get('User-Agent')
      });

      res.json({ message: 'Components reordered successfully' });
    } catch (error) {
      console.error('Error reordering components:', error);
      res.status(500).json({ message: 'Failed to reorder components' });
    }
  });

  // AI-powered content generation
  app.post('/api/admin/generate/component', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const request = req.body;
      const generatedComponent = await generatePageComponent(request);

      // Log user activity
      await storage.createUserActivity({
        adminUserId: req.adminUser?.id,
        activity: 'Generated AI component',
        details: { componentType: request.componentType, industry: request.industry },
        ipAddress: req.ip,
        userAgent: req.get('User-Agent')
      });

      res.json(generatedComponent);
    } catch (error) {
      console.error('Error generating component:', error);
      res.status(500).json({ message: 'Failed to generate component' });
    }
  });

  // Generate full page with AI
  app.post('/api/admin/generate/page', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const request = req.body;
      const generatedComponents = await generateFullPage(request);

      // Log user activity
      await storage.createUserActivity({
        adminUserId: req.adminUser?.id,
        activity: 'Generated AI page',
        details: { pageType: request.pageType, title: request.title },
        ipAddress: req.ip,
        userAgent: req.get('User-Agent')
      });

      res.json(generatedComponents);
    } catch (error) {
      console.error('Error generating page:', error);
      res.status(500).json({ message: 'Failed to generate page' });
    }
  });

  // Page templates
  app.get('/api/admin/templates', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const templates = await storage.getPageTemplates();
      res.json(templates);
    } catch (error) {
      console.error('Error fetching templates:', error);
      res.status(500).json({ message: 'Failed to fetch templates' });
    }
  });

  app.post('/api/admin/templates', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const templateData = insertPageTemplateSchema.parse({ ...req.body, createdBy: req.adminUser?.id });
      const newTemplate = await storage.createPageTemplate(templateData);

      // Log user activity
      await storage.createUserActivity({
        adminUserId: req.adminUser?.id,
        activity: 'Created page template',
        details: { templateId: newTemplate.id, name: newTemplate.name },
        ipAddress: req.ip,
        userAgent: req.get('User-Agent')
      });

      res.status(201).json(newTemplate);
    } catch (error) {
      console.error('Error creating template:', error);
      res.status(500).json({ message: 'Failed to create template' });
    }
  });

  // Import existing pages endpoint
  app.post('/api/admin/import-pages', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      // Import existing website pages into the page builder
      const { importExistingPages } = await import('../scripts/import-existing-pages');
      await importExistingPages();

      // Log user activity
      await storage.createUserActivity({
        adminUserId: req.adminUser?.id,
        activity: 'Imported existing pages',
        details: { action: 'bulk_import' },
        ipAddress: req.ip,
        userAgent: req.get('User-Agent')
      });

      res.json({ message: 'Successfully imported existing pages' });
    } catch (error) {
      console.error('Error importing pages:', error);
      res.status(500).json({ message: 'Failed to import pages' });
    }
  });

  // Get page content for editing
  app.get('/api/admin/pages/:slugOrId/edit', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const { slugOrId } = req.params;
      let page;

      // Check if it's a numeric ID or slug
      if (!isNaN(Number(slugOrId))) {
        page = await storage.getPageById(Number(slugOrId));
      } else {
        page = await storage.getPageBySlug(slugOrId);
      }

      if (!page) {
        return res.status(404).json({ message: 'Page not found' });
      }

      // Get page components
      const components = await storage.getPageComponents(page.id);

      res.json({
        page,
        components: components.sort((a, b) => a.sortOrder - b.sortOrder)
      });
    } catch (error) {
      console.error('Error fetching page for editing:', error);
      res.status(500).json({ message: 'Failed to fetch page' });
    }
  });

  // ========== DASHBOARD WIDGET CUSTOMIZATION API ROUTES ==========

  // Get available dashboard widgets
  app.get('/api/admin/dashboard/widgets', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const { category } = req.query;
      const widgets = await storage.getDashboardWidgets(category as string);
      res.json(widgets);
    } catch (error) {
      console.error('Error fetching dashboard widgets:', error);
      res.status(500).json({ message: 'Failed to fetch dashboard widgets' });
    }
  });

  // Create new dashboard widget
  app.post('/api/admin/dashboard/widgets', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const widgetData = insertDashboardWidgetSchema.parse(req.body);
      const newWidget = await storage.createDashboardWidget(widgetData);

      // Log user activity
      await storage.createUserActivity({
        adminUserId: req.adminUser?.id,
        activity: 'Created dashboard widget',
        details: { widgetId: newWidget.id, name: newWidget.name },
        ipAddress: req.ip,
        userAgent: req.get('User-Agent')
      });

      res.status(201).json(newWidget);
    } catch (error) {
      console.error('Error creating dashboard widget:', error);
      res.status(500).json({ message: 'Failed to create dashboard widget' });
    }
  });

  // Get user dashboards
  app.get('/api/admin/user-dashboards', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const dashboards = await storage.getUserDashboards(req.adminUser!.id);
      res.json(dashboards);
    } catch (error) {
      console.error('Error fetching user dashboards:', error);
      res.status(500).json({ message: 'Failed to fetch user dashboards' });
    }
  });

  // Create new user dashboard
  app.post('/api/admin/user-dashboards', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const dashboardData = insertUserDashboardSchema.parse({ 
        ...req.body, 
        adminUserId: req.adminUser!.id 
      });
      const newDashboard = await storage.createUserDashboard(dashboardData);

      // Log user activity
      await storage.createUserActivity({
        adminUserId: req.adminUser?.id,
        activity: 'Created user dashboard',
        details: { dashboardId: newDashboard.id, name: newDashboard.name },
        ipAddress: req.ip,
        userAgent: req.get('User-Agent')
      });

      res.status(201).json(newDashboard);
    } catch (error) {
      console.error('Error creating user dashboard:', error);
      res.status(500).json({ message: 'Failed to create user dashboard' });
    }
  });

  // Update user dashboard
  app.put('/api/admin/user-dashboards/:id', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const dashboardId = parseInt(req.params.id);
      const updates = req.body;
      const updatedDashboard = await storage.updateUserDashboard(dashboardId, updates);

      // Log user activity
      await storage.createUserActivity({
        adminUserId: req.adminUser?.id,
        activity: 'Updated user dashboard',
        details: { dashboardId, changes: Object.keys(updates) },
        ipAddress: req.ip,
        userAgent: req.get('User-Agent')
      });

      res.json(updatedDashboard);
    } catch (error) {
      console.error('Error updating user dashboard:', error);
      res.status(500).json({ message: 'Failed to update user dashboard' });
    }
  });

  // Set default dashboard
  app.post('/api/admin/user-dashboards/:id/set-default', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const dashboardId = parseInt(req.params.id);
      await storage.setDefaultDashboard(dashboardId, req.adminUser!.id);

      // Log user activity
      await storage.createUserActivity({
        adminUserId: req.adminUser?.id,
        activity: 'Set default dashboard',
        details: { dashboardId },
        ipAddress: req.ip,
        userAgent: req.get('User-Agent')
      });

      res.json({ message: 'Default dashboard set successfully' });
    } catch (error) {
      console.error('Error setting default dashboard:', error);
      res.status(500).json({ message: 'Failed to set default dashboard' });
    }
  });

  // Get dashboard widget instances
  app.get('/api/admin/dashboards/:id/widgets', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const dashboardId = parseInt(req.params.id);
      const widgets = await storage.getDashboardWidgetInstances(dashboardId);
      res.json(widgets);
    } catch (error) {
      console.error('Error fetching dashboard widget instances:', error);
      res.status(500).json({ message: 'Failed to fetch dashboard widget instances' });
    }
  });

  // Add widget to dashboard
  app.post('/api/admin/dashboards/:id/widgets', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const dashboardId = parseInt(req.params.id);
      const instanceData = insertDashboardWidgetInstanceSchema.parse({ 
        ...req.body, 
        dashboardId 
      });
      const newInstance = await storage.createDashboardWidgetInstance(instanceData);

      // Log user activity
      await storage.createUserActivity({
        adminUserId: req.adminUser?.id,
        activity: 'Added widget to dashboard',
        details: { dashboardId, widgetInstanceId: newInstance.id },
        ipAddress: req.ip,
        userAgent: req.get('User-Agent')
      });

      res.status(201).json(newInstance);
    } catch (error) {
      console.error('Error adding widget to dashboard:', error);
      res.status(500).json({ message: 'Failed to add widget to dashboard' });
    }
  });

  // Update widget instance
  app.put('/api/admin/widget-instances/:id', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const instanceId = parseInt(req.params.id);
      const updates = req.body;
      const updatedInstance = await storage.updateDashboardWidgetInstance(instanceId, updates);

      // Log user activity
      await storage.createUserActivity({
        adminUserId: req.adminUser?.id,
        activity: 'Updated widget instance',
        details: { instanceId, changes: Object.keys(updates) },
        ipAddress: req.ip,
        userAgent: req.get('User-Agent')
      });

      res.json(updatedInstance);
    } catch (error) {
      console.error('Error updating widget instance:', error);
      res.status(500).json({ message: 'Failed to update widget instance' });
    }
  });

  // Delete widget instance
  app.delete('/api/admin/widget-instances/:id', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const instanceId = parseInt(req.params.id);
      await storage.deleteDashboardWidgetInstance(instanceId);

      // Log user activity
      await storage.createUserActivity({
        adminUserId: req.adminUser?.id,
        activity: 'Deleted widget instance',
        details: { instanceId },
        ipAddress: req.ip,
        userAgent: req.get('User-Agent')
      });

      res.json({ message: 'Widget instance deleted successfully' });
    } catch (error) {
      console.error('Error deleting widget instance:', error);
      res.status(500).json({ message: 'Failed to delete widget instance' });
    }
  });

  // Update widget positions (for drag and drop)
  app.put('/api/admin/dashboards/:id/widgets/positions', authenticateAdmin, async (req: AuthenticatedRequest, res) => {
    try {
      const dashboardId = parseInt(req.params.id);
      const { positions } = req.body;
      await storage.updateWidgetPositions(dashboardId, positions);

      // Log user activity
      await storage.createUserActivity({
        adminUserId: req.adminUser?.id,
        activity: 'Updated widget positions',
        details: { dashboardId, widgetCount: positions.length },
        ipAddress: req.ip,
        userAgent: req.get('User-Agent')
      });

      res.json({ message: 'Widget positions updated successfully' });
    } catch (error) {
      console.error('Error updating widget positions:', error);
      res.status(500).json({ message: 'Failed to update widget positions' });
    }
  });

  const httpServer = createServer(app);

  return httpServer;
}

server/storage.ts.content:
import { 
  users, 
  adminUsers,
  contactSubmissions,
  pageViews,
  emailCampaigns,
  contentBlocks,
  userSessions,
  aiInteractions,
  systemLogs,
  integrations,
  userActivities,
  pages,
  pageComponents,
  pageTemplates,
  dashboardWidgets,
  userDashboards,
  dashboardWidgetInstances,
  type User, 
  type InsertUser,
  type AdminUser,
  type InsertAdminUser,
  type ContactSubmission,
  type InsertContactSubmission,
  type PageView,
  type InsertPageView,
  type EmailCampaign,
  type InsertEmailCampaign,
  type ContentBlock,
  type InsertContentBlock,
  type UserSession,
  type AiInteraction,
  type SystemLog,
  type Integration,
  type InsertIntegration,
  type UserActivity,
  type InsertUserActivity,
  type Page,
  type InsertPage,
  type PageComponent,
  type InsertPageComponent,
  type PageTemplate,
  type InsertPageTemplate,
  type DashboardWidget,
  type InsertDashboardWidget,
  type UserDashboard,
  type InsertUserDashboard,
  type DashboardWidgetInstance,
  type InsertDashboardWidgetInstance
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, count, and, gte, sql, asc } from "drizzle-orm";

export interface IStorage {
  // Basic user operations
  getUser(id: number): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Admin user operations
  getAdminUser(id: number): Promise<AdminUser | undefined>;
  getAdminUserByEmail(email: string): Promise<AdminUser | undefined>;
  createAdminUser(user: InsertAdminUser): Promise<AdminUser>;
  updateAdminUserLastLogin(id: number): Promise<void>;

  // Contact submissions
  createContactSubmission(submission: InsertContactSubmission): Promise<ContactSubmission>;
  getContactSubmissions(limit?: number, offset?: number): Promise<ContactSubmission[]>;
  updateContactSubmissionStatus(id: number, status: string): Promise<ContactSubmission>;
  getContactSubmissionsCount(): Promise<number>;

  // Analytics and tracking
  createPageView(pageView: InsertPageView): Promise<PageView>;
  getPageViewsCount(startDate?: Date, endDate?: Date): Promise<number>;
  getTopPages(limit?: number): Promise<{path: string, count: number}[]>;
  getDeviceStats(): Promise<{device: string, count: number}[]>;
  getCountryStats(): Promise<{country: string, count: number}[]>;

  // Email campaigns
  createEmailCampaign(campaign: InsertEmailCampaign): Promise<EmailCampaign>;
  getEmailCampaigns(): Promise<EmailCampaign[]>;
  updateEmailCampaign(id: number, updates: Partial<EmailCampaign>): Promise<EmailCampaign>;
  deleteEmailCampaign(id: number): Promise<void>;

  // Content management
  createContentBlock(content: InsertContentBlock): Promise<ContentBlock>;
  getContentBlocks(page?: string): Promise<ContentBlock[]>;
  updateContentBlock(id: number, updates: Partial<ContentBlock>): Promise<ContentBlock>;
  getContentBlockByKey(key: string): Promise<ContentBlock | undefined>;

  // User sessions
  createUserSession(session: Partial<UserSession>): Promise<UserSession>;
  updateUserSession(sessionId: string, updates: Partial<UserSession>): Promise<void>;

  // AI interactions
  createAiInteraction(interaction: Partial<AiInteraction>): Promise<AiInteraction>;
  getAiInteractionStats(): Promise<{totalInteractions: number, clickedRecommendations: number}>;

  // System logs
  createSystemLog(log: Partial<SystemLog>): Promise<SystemLog>;
  getSystemLogs(level?: string, limit?: number): Promise<SystemLog[]>;

  // Integrations
  createIntegration(integration: InsertIntegration): Promise<Integration>;
  getIntegrations(): Promise<Integration[]>;
  updateIntegration(id: number, updates: Partial<Integration>): Promise<Integration>;
  deleteIntegration(id: number): Promise<void>;
  getIntegrationById(id: number): Promise<Integration | undefined>;

  // User activities
  createUserActivity(activity: InsertUserActivity): Promise<UserActivity>;
  getUserActivities(adminUserId?: number, limit?: number): Promise<UserActivity[]>;

  // Admin user management
  getAllAdminUsers(): Promise<AdminUser[]>;
  updateAdminUser(id: number, updates: Partial<AdminUser>): Promise<AdminUser>;
  deleteAdminUser(id: number): Promise<void>;

  // Dashboard analytics
  getDashboardStats(): Promise<{
    totalPageViews: number;
    totalContacts: number;
    totalSessions: number;
    aiInteractions: number;
  }>;

  // Page builder operations
  createPage(page: InsertPage): Promise<Page>;
  getPages(status?: string): Promise<Page[]>;
  getPageById(id: number): Promise<Page | undefined>;
  getPageBySlug(slug: string): Promise<Page | undefined>;
  updatePage(id: number, updates: Partial<Page>): Promise<Page>;
  deletePage(id: number): Promise<void>;

  // Page components operations
  createPageComponent(component: InsertPageComponent): Promise<PageComponent>;
  getPageComponents(pageId: number): Promise<PageComponent[]>;
  updatePageComponent(id: number, updates: Partial<PageComponent>): Promise<PageComponent>;
  deletePageComponent(id: number): Promise<void>;
  reorderPageComponents(pageId: number, componentIds: number[]): Promise<void>;

  // Page templates operations
  createPageTemplate(template: InsertPageTemplate): Promise<PageTemplate>;
  getPageTemplates(): Promise<PageTemplate[]>;
  getPageTemplateById(id: number): Promise<PageTemplate | undefined>;
  updatePageTemplate(id: number, updates: Partial<PageTemplate>): Promise<PageTemplate>;
  deletePageTemplate(id: number): Promise<void>;

  // Dashboard widget operations
  createDashboardWidget(widget: InsertDashboardWidget): Promise<DashboardWidget>;
  getDashboardWidgets(category?: string): Promise<DashboardWidget[]>;
  getDashboardWidgetById(id: number): Promise<DashboardWidget | undefined>;
  updateDashboardWidget(id: number, updates: Partial<DashboardWidget>): Promise<DashboardWidget>;
  deleteDashboardWidget(id: number): Promise<void>;

  // User dashboard operations
  createUserDashboard(dashboard: InsertUserDashboard): Promise<UserDashboard>;
  getUserDashboards(adminUserId: number): Promise<UserDashboard[]>;
  getUserDashboardById(id: number): Promise<UserDashboard | undefined>;
  updateUserDashboard(id: number, updates: Partial<UserDashboard>): Promise<UserDashboard>;
  deleteUserDashboard(id: number): Promise<void>;
  setDefaultDashboard(dashboardId: number, adminUserId: number): Promise<void>;

  // Dashboard widget instances operations
  createDashboardWidgetInstance(instance: InsertDashboardWidgetInstance): Promise<DashboardWidgetInstance>;
  getDashboardWidgetInstances(dashboardId: number): Promise<DashboardWidgetInstance[]>;
  updateDashboardWidgetInstance(id: number, updates: Partial<DashboardWidgetInstance>): Promise<DashboardWidgetInstance>;
  deleteDashboardWidgetInstance(id: number): Promise<void>;
  updateWidgetPositions(dashboardId: number, positions: Array<{id: number, position: any}>): Promise<void>;
}

export class DatabaseStorage implements IStorage {
  // Basic user operations
  async getUser(id: number): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db.insert(users).values(insertUser).returning();
    return user;
  }

  // Admin user operations
  async getAdminUser(id: number): Promise<AdminUser | undefined> {
    const [admin] = await db.select().from(adminUsers).where(eq(adminUsers.id, id));
    return admin || undefined;
  }

  async getAdminUserByEmail(email: string): Promise<AdminUser | undefined> {
    const [admin] = await db.select().from(adminUsers).where(eq(adminUsers.email, email));
    return admin || undefined;
  }

  async createAdminUser(insertAdminUser: InsertAdminUser): Promise<AdminUser> {
    const [admin] = await db.insert(adminUsers).values(insertAdminUser).returning();
    return admin;
  }

  async updateAdminUserLastLogin(id: number): Promise<void> {
    await db.update(adminUsers)
      .set({ lastLogin: new Date() })
      .where(eq(adminUsers.id, id));
  }

  // Contact submissions
  async createContactSubmission(submission: InsertContactSubmission): Promise<ContactSubmission> {
    const [contact] = await db.insert(contactSubmissions).values(submission).returning();
    return contact;
  }

  async getContactSubmissions(limit = 50, offset = 0): Promise<ContactSubmission[]> {
    return await db.select()
      .from(contactSubmissions)
      .orderBy(desc(contactSubmissions.createdAt))
      .limit(limit)
      .offset(offset);
  }

  async updateContactSubmissionStatus(id: number, status: string): Promise<ContactSubmission> {
    const [updated] = await db.update(contactSubmissions)
      .set({ status, updatedAt: new Date() })
      .where(eq(contactSubmissions.id, id))
      .returning();
    return updated;
  }

  async getContactSubmissionsCount(): Promise<number> {
    const [result] = await db.select({ count: count() }).from(contactSubmissions);
    return result.count;
  }

  // Analytics and tracking
  async createPageView(pageView: InsertPageView): Promise<PageView> {
    const [view] = await db.insert(pageViews).values(pageView).returning();
    return view;
  }

  async getPageViewsCount(startDate?: Date, endDate?: Date): Promise<number> {
    let query = db.select({ count: count() }).from(pageViews);

    if (startDate && endDate) {
      query = query.where(and(
        gte(pageViews.timestamp, startDate),
        sql`${pageViews.timestamp} <= ${endDate}`
      ));
    }

    const [result] = await query;
    return result.count;
  }

  async getTopPages(limit = 10): Promise<{path: string, count: number}[]> {
    return await db.select({
      path: pageViews.path,
      count: count()
    })
    .from(pageViews)
    .groupBy(pageViews.path)
    .orderBy(desc(count()))
    .limit(limit);
  }

  async getDeviceStats(): Promise<{device: string, count: number}[]> {
    return await db.select({
      device: pageViews.device,
      count: count()
    })
    .from(pageViews)
    .where(sql`${pageViews.device} IS NOT NULL`)
    .groupBy(pageViews.device)
    .orderBy(desc(count()));
  }

  async getCountryStats(): Promise<{country: string, count: number}[]> {
    return await db.select({
      country: pageViews.country,
      count: count()
    })
    .from(pageViews)
    .where(sql`${pageViews.country} IS NOT NULL`)
    .groupBy(pageViews.country)
    .orderBy(desc(count()))
    .limit(10);
  }

  // Email campaigns
  async createEmailCampaign(campaign: InsertEmailCampaign): Promise<EmailCampaign> {
    const [email] = await db.insert(emailCampaigns).values(campaign).returning();
    return email;
  }

  async getEmailCampaigns(): Promise<EmailCampaign[]> {
    return await db.select().from(emailCampaigns).orderBy(desc(emailCampaigns.createdAt));
  }

  async updateEmailCampaign(id: number, updates: Partial<EmailCampaign>): Promise<EmailCampaign> {
    const [updated] = await db.update(emailCampaigns)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(emailCampaigns.id, id))
      .returning();
    return updated;
  }

  async deleteEmailCampaign(id: number): Promise<void> {
    await db.delete(emailCampaigns).where(eq(emailCampaigns.id, id));
  }

  // Content management
  async createContentBlock(content: InsertContentBlock): Promise<ContentBlock> {
    const [block] = await db.insert(contentBlocks).values(content).returning();
    return block;
  }

  async getContentBlocks(page?: string): Promise<ContentBlock[]> {
    let query = db.select().from(contentBlocks).where(eq(contentBlocks.isActive, true));

    if (page) {
      query = query.where(eq(contentBlocks.page, page));
    }

    return await query.orderBy(contentBlocks.createdAt);
  }

  async updateContentBlock(id: number, updates: Partial<ContentBlock>): Promise<ContentBlock> {
    const [updated] = await db.update(contentBlocks)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(contentBlocks.id, id))
      .returning();
    return updated;
  }

  async getContentBlockByKey(key: string): Promise<ContentBlock | undefined> {
    const [block] = await db.select().from(contentBlocks).where(eq(contentBlocks.key, key));
    return block || undefined;
  }

  // User sessions
  async createUserSession(session: Partial<UserSession>): Promise<UserSession> {
    const [userSession] = await db.insert(userSessions).values(session as any).returning();
    return userSession;
  }

  async updateUserSession(sessionId: string, updates: Partial<UserSession>): Promise<void> {
    await db.update(userSessions)
      .set(updates)
      .where(eq(userSessions.sessionId, sessionId));
  }

  // AI interactions
  async createAiInteraction(interaction: Partial<AiInteraction>): Promise<AiInteraction> {
    const [ai] = await db.insert(aiInteractions).values(interaction as any).returning();
    return ai;
  }

  async getAiInteractionStats(): Promise<{totalInteractions: number, clickedRecommendations: number}> {
    const [total] = await db.select({ count: count() }).from(aiInteractions);
    const [clicked] = await db.select({ count: count() })
      .from(aiInteractions)
      .where(sql`${aiInteractions.clickedRecommendation} IS NOT NULL`);

    return {
      totalInteractions: total.count,
      clickedRecommendations: clicked.count
    };
  }

  // System logs
  async createSystemLog(log: Partial<SystemLog>): Promise<SystemLog> {
    const [systemLog] = await db.insert(systemLogs).values(log as any).returning();
    return systemLog;
  }

  async getSystemLogs(level?: string, limit = 100): Promise<SystemLog[]> {
    let query = db.select().from(systemLogs);

    if (level) {
      query = query.where(eq(systemLogs.level, level));
    }

    return await query.orderBy(desc(systemLogs.timestamp)).limit(limit);
  }

  // Integrations
  async createIntegration(integration: InsertIntegration): Promise<Integration> {
    const [newIntegration] = await db.insert(integrations).values(integration).returning();
    return newIntegration;
  }

  async getIntegrations(): Promise<Integration[]> {
    return await db.select().from(integrations).orderBy(desc(integrations.createdAt));
  }

  async updateIntegration(id: number, updates: Partial<Integration>): Promise<Integration> {
    const [updated] = await db.update(integrations)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(integrations.id, id))
      .returning();
    return updated;
  }

  async deleteIntegration(id: number): Promise<void> {
    await db.delete(integrations).where(eq(integrations.id, id));
  }

  async getIntegrationById(id: number): Promise<Integration | undefined> {
    const [integration] = await db.select().from(integrations).where(eq(integrations.id, id));
    return integration || undefined;
  }

  // User activities
  async createUserActivity(activity: InsertUserActivity): Promise<UserActivity> {
    const [newActivity] = await db.insert(userActivities).values(activity).returning();
    return newActivity;
  }

  async getUserActivities(adminUserId?: number, limit = 100): Promise<UserActivity[]> {
    let query = db.select().from(userActivities);

    if (adminUserId) {
      query = query.where(eq(userActivities.adminUserId, adminUserId));
    }

    return await query.orderBy(desc(userActivities.createdAt)).limit(limit);
  }

  // Admin user management
  async getAllAdminUsers(): Promise<AdminUser[]> {
    return await db.select().from(adminUsers).orderBy(asc(adminUsers.name));
  }

  async updateAdminUser(id: number, updates: Partial<AdminUser>): Promise<AdminUser> {
    const [updated] = await db.update(adminUsers)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(adminUsers.id, id))
      .returning();
    return updated;
  }

  async deleteAdminUser(id: number): Promise<void> {
    await db.delete(adminUsers).where(eq(adminUsers.id, id));
  }

  // Dashboard analytics
  async getDashboardStats(): Promise<{
    totalPageViews: number;
    totalContacts: number;
    totalSessions: number;
    aiInteractions: number;
  }> {
    const [pageViewCount] = await db.select({ count: count() }).from(pageViews);
    const [contactCount] = await db.select({ count: count() }).from(contactSubmissions);
    const [sessionCount] = await db.select({ count: count() }).from(userSessions);
    const [aiCount] = await db.select({ count: count() }).from(aiInteractions);

    return {
      totalPageViews: pageViewCount.count,
      totalContacts: contactCount.count,
      totalSessions: sessionCount.count,
      aiInteractions: aiCount.count
    };
  }

  // Page builder operations
  async createPage(insertPage: InsertPage): Promise<Page> {
    const [page] = await db
      .insert(pages)
      .values(insertPage)
      .returning();
    return page;
  }

  async getPages(status?: string): Promise<Page[]> {
    if (status) {
      return await db.select().from(pages).where(eq(pages.status, status)).orderBy(desc(pages.updatedAt));
    }
    return await db.select().from(pages).orderBy(desc(pages.updatedAt));
  }

  async getPageById(id: number): Promise<Page | undefined> {
    const [page] = await db.select().from(pages).where(eq(pages.id, id));
    return page;
  }

  async getPageBySlug(slug: string): Promise<Page | undefined> {
    const [page] = await db.select().from(pages).where(eq(pages.slug, slug));
    return page;
  }

  async updatePage(id: number, updates: Partial<Page>): Promise<Page> {
    const [page] = await db
      .update(pages)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(pages.id, id))
      .returning();
    return page;
  }

  async deletePage(id: number): Promise<void> {
    await db.delete(pages).where(eq(pages.id, id));
  }

  // Page components operations
  async createPageComponent(component: InsertPageComponent): Promise<PageComponent> {
    const [pageComponent] = await db
      .insert(pageComponents)
      .values(component)
      .returning();
    return pageComponent;
  }

  async getPageComponents(pageId: number): Promise<PageComponent[]> {
    return await db
      .select()
      .from(pageComponents)
      .where(eq(pageComponents.pageId, pageId))
      .orderBy(pageComponents.sortOrder);
  }

  async updatePageComponent(id: number, updates: Partial<PageComponent>): Promise<PageComponent> {
    const [component] = await db
      .update(pageComponents)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(pageComponents.id, id))
      .returning();
    return component;
  }

  async deletePageComponent(id: number): Promise<void> {
    await db.delete(pageComponents).where(eq(pageComponents.id, id));
  }

  async reorderPageComponents(pageId: number, componentIds: number[]): Promise<void> {
    for (let i = 0; i < componentIds.length; i++) {
      await db
        .update(pageComponents)
        .set({ sortOrder: i, updatedAt: new Date() })
        .where(and(eq(pageComponents.id, componentIds[i]), eq(pageComponents.pageId, pageId)));
    }
  }

  // Page templates operations
  async createPageTemplate(template: InsertPageTemplate): Promise<PageTemplate> {
    const [pageTemplate] = await db
      .insert(pageTemplates)
      .values(template)
      .returning();
    return pageTemplate;
  }

  async getPageTemplates(): Promise<PageTemplate[]> {
    return await db.select().from(pageTemplates).where(eq(pageTemplates.isActive, true));
  }

  async getPageTemplateById(id: number): Promise<PageTemplate | undefined> {
    const [template] = await db.select().from(pageTemplates).where(eq(pageTemplates.id, id));
    return template;
  }

  async updatePageTemplate(id: number, updates: Partial<PageTemplate>): Promise<PageTemplate> {
    const [template] = await db
      .update(pageTemplates)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(pageTemplates.id, id))
      .returning();
    return template;
  }

  async deletePageTemplate(id: number): Promise<void> {
    await db.delete(pageTemplates).where(eq(pageTemplates.id, id));
  }

  // Dashboard widget operations
  async createDashboardWidget(widget: InsertDashboardWidget): Promise<DashboardWidget> {
    const [dashboardWidget] = await db
      .insert(dashboardWidgets)
      .values(widget)
      .returning();
    return dashboardWidget;
  }

  async getDashboardWidgets(category?: string): Promise<DashboardWidget[]> {
    if (category) {
      return await db
        .select()
        .from(dashboardWidgets)
        .where(and(eq(dashboardWidgets.category, category), eq(dashboardWidgets.isActive, true)))
        .orderBy(dashboardWidgets.name);
    }
    return await db
      .select()
      .from(dashboardWidgets)
      .where(eq(dashboardWidgets.isActive, true))
      .orderBy(dashboardWidgets.category, dashboardWidgets.name);
  }

  async getDashboardWidgetById(id: number): Promise<DashboardWidget | undefined> {
    const [widget] = await db.select().from(dashboardWidgets).where(eq(dashboardWidgets.id, id));
    return widget;
  }

  async updateDashboardWidget(id: number, updates: Partial<DashboardWidget>): Promise<DashboardWidget> {
    const [widget] = await db
      .update(dashboardWidgets)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(dashboardWidgets.id, id))
      .returning();
    return widget;
  }

  async deleteDashboardWidget(id: number): Promise<void> {
    await db.delete(dashboardWidgets).where(eq(dashboardWidgets.id, id));
  }

  // User dashboard operations
  async createUserDashboard(dashboard: InsertUserDashboard): Promise<UserDashboard> {
    const [userDashboard] = await db
      .insert(userDashboards)
      .values(dashboard)
      .returning();
    return userDashboard;
  }

  async getUserDashboards(adminUserId: number): Promise<UserDashboard[]> {
    return await db
      .select()
      .from(userDashboards)
      .where(eq(userDashboards.adminUserId, adminUserId))
      .orderBy(desc(userDashboards.isDefault), userDashboards.name);
  }

  async getUserDashboardById(id: number): Promise<UserDashboard | undefined> {
    const [dashboard] = await db.select().from(userDashboards).where(eq(userDashboards.id, id));
    return dashboard;
  }

  async updateUserDashboard(id: number, updates: Partial<UserDashboard>): Promise<UserDashboard> {
    const [dashboard] = await db
      .update(userDashboards)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(userDashboards.id, id))
      .returning();
    return dashboard;
  }

  async deleteUserDashboard(id: number): Promise<void> {
    await db.delete(userDashboards).where(eq(userDashboards.id, id));
  }

  async setDefaultDashboard(dashboardId: number, adminUserId: number): Promise<void> {
    // First, unset all default dashboards for this user
    await db
      .update(userDashboards)
      .set({ isDefault: false, updatedAt: new Date() })
      .where(eq(userDashboards.adminUserId, adminUserId));

    // Then set the specified dashboard as default
    await db
      .update(userDashboards)
      .set({ isDefault: true, updatedAt: new Date() })
      .where(and(eq(userDashboards.id, dashboardId), eq(userDashboards.adminUserId, adminUserId)));
  }

  // Dashboard widget instances operations
  async createDashboardWidgetInstance(instance: InsertDashboardWidgetInstance): Promise<DashboardWidgetInstance> {
    const [widgetInstance] = await db
      .insert(dashboardWidgetInstances)
      .values(instance)
      .returning();
    return widgetInstance;
  }

  async getDashboardWidgetInstances(dashboardId: number): Promise<DashboardWidgetInstance[]> {
    return await db
      .select()
      .from(dashboardWidgetInstances)
      .where(eq(dashboardWidgetInstances.dashboardId, dashboardId))
      .orderBy(sql`(${dashboardWidgetInstances.position}->>'x')::int, (${dashboardWidgetInstances.position}->>'y')::int`);
  }

  async updateDashboardWidgetInstance(id: number, updates: Partial<DashboardWidgetInstance>): Promise<DashboardWidgetInstance> {
    const [instance] = await db
      .update(dashboardWidgetInstances)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(dashboardWidgetInstances.id, id))
      .returning();
    return instance;
  }

  async deleteDashboardWidgetInstance(id: number): Promise<void> {
    await db.delete(dashboardWidgetInstances).where(eq(dashboardWidgetInstances.id, id));
  }

  async updateWidgetPositions(dashboardId: number, positions: Array<{id: number, position: any}>): Promise<void> {
    for (const pos of positions) {
      await db
        .update(dashboardWidgetInstances)
        .set({ position: pos.position, updatedAt: new Date() })
        .where(and(eq(dashboardWidgetInstances.id, pos.id), eq(dashboardWidgetInstances.dashboardId, dashboardId)));
    }
  }
}

export const storage = new DatabaseStorage();

