import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";
import { errorHandler, notFoundHandler } from "./errorHandler";

const app = express();

// Enhanced security headers and restricted CORS
app.use((req, res, next) => {
  // Restricted CORS policy - only allow specific trusted domains
  const allowedOrigins = [
    'https://asterik.ae',
    'https://www.asterik.ae',
    process.env.NODE_ENV === 'development' ? 'http://localhost:5000' : null,
    (process.env.REPL_SLUG && process.env.REPL_OWNER) ? `https://${process.env.REPL_SLUG}.${process.env.REPL_OWNER}.repl.co` : null
  ].filter(Boolean);

  const origin = req.headers.origin;
  if (origin && allowedOrigins.includes(origin)) {
    res.header('Access-Control-Allow-Origin', origin);
  }

  res.header('Access-Control-Allow-Methods', 'GET, POST, PUT, DELETE, OPTIONS');
  res.header('Access-Control-Allow-Headers', 'Origin, X-Requested-With, Content-Type, Accept, Authorization');
  res.header('Access-Control-Allow-Credentials', 'true');

  // Enhanced security headers
  res.header('X-Content-Type-Options', 'nosniff');
  res.header('X-Frame-Options', 'SAMEORIGIN');
  res.header('X-XSS-Protection', '1; mode=block');
  res.header('Referrer-Policy', 'strict-origin-when-cross-origin');
  res.header('Permissions-Policy', 'camera=(), microphone=(), geolocation=()');

  // Content Security Policy
  res.header('Content-Security-Policy', [
    "default-src 'self'",
    "script-src 'self' 'unsafe-inline' 'unsafe-eval' blob: https://sibforms.com https://js.brevo.com https://cdn.brevo.com https://sibautomation.com https://replit.com",
    "style-src 'self' 'unsafe-inline' https://fonts.googleapis.com https://cdnjs.cloudflare.com",
    "font-src 'self' https://fonts.gstatic.com https://cdnjs.cloudflare.com data:",
    "img-src 'self' data: https:",
    "connect-src 'self' https://sibforms.com https://api.brevo.com https://cdn.brevo.com",
    "frame-src https://sibforms.com",
    "object-src 'none'",
    "base-uri 'self'"
  ].join('; '));

  // HSTS for HTTPS
  if (req.secure || req.headers['x-forwarded-proto'] === 'https') {
    res.header('Strict-Transport-Security', 'max-age=31536000; includeSubDomains; preload');
  }

  // Rate limiting headers
  res.header('X-Rate-Limit', '1000');
  res.header('Cache-Control', 'no-cache, no-store, must-revalidate');
  res.header('Pragma', 'no-cache');
  res.header('Expires', '0');

  if (req.method === 'OPTIONS') {
    res.sendStatus(200);
  } else {
    next();
  }
});

app.use(express.json());
app.use(express.urlencoded({ extended: false }));

// Serve static files from root directory for domain verification and Brevo files
app.use(express.static('.'));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  // Use comprehensive error handler
  app.use(errorHandler);

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on port 5000
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = 5000;
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertContactSubmissionSchema, insertEmailCampaignSchema } from "@shared/schema";
import { z } from "zod";
import bcrypt from "bcrypt";
import jwt from "jsonwebtoken";
import type { Request, Response, NextFunction } from "express";
import path from "path";
import { emailService } from "./emailService";
import { asyncHandler, AuthenticationError, ValidationError } from "./errorHandler";

// Auth utilities
const JWT_SECRET = process.env.JWT_SECRET!;
if (!JWT_SECRET) {
  throw new Error('JWT_SECRET environment variable is required for security');
}

export async function hashPassword(password: string): Promise<string> {
  return await bcrypt.hash(password, 12);
}

export async function verifyPassword(password: string, hash: string): Promise<boolean> {
  return await bcrypt.compare(password, hash);
}

export function generateToken(payload: any): string {
  return jwt.sign(payload, JWT_SECRET, { expiresIn: "24h" });
}

interface AuthenticatedRequest extends Request {
  user?: any;
}

export async function registerRoutes(app: Express): Promise<Server> {

  // Production health check endpoint only
  app.get("/api/health", (req, res) => {
    res.json({ status: "ok", timestamp: new Date().toISOString() });
  });



  // Enhanced input validation schema
  const contactValidationSchema = insertContactSubmissionSchema.extend({
    name: z.string().min(2).max(100).regex(/^[a-zA-Z\s\-'\.]+$/, "Invalid characters in name"),
    email: z.string().email().max(255),
    company: z.string().max(200).optional(),
    phone: z.string().max(20).regex(/^[\+]?[0-9\s\-\(\)]+$/, "Invalid phone format").optional(),
    subject: z.string().max(300).optional(),
    message: z.string().min(10).max(5000)
  });

  // Contact form submission with comprehensive validation
  app.post("/api/contact", asyncHandler(async (req, res) => {
    try {
      // Rate limiting check (basic)
      const clientIP = req.ip || req.connection.remoteAddress;

      // Sanitize and validate input
      const validatedData = contactValidationSchema.parse(req.body);

      // Additional security checks
      const suspiciousPatterns = [
        /<script/i, /javascript:/i, /on\w+=/i, /data:text\/html/i,
        /\bsql\b.*\b(union|select|insert|update|delete)\b/i
      ];

      const allText = Object.values(validatedData).join(' ');
      if (suspiciousPatterns.some(pattern => pattern.test(allText))) {
        return res.status(400).json({ error: "Invalid content detected" });
      }

      const submission = await storage.createContactSubmission(validatedData);
      res.json({ success: true, id: submission.id });
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ 
          error: "Validation failed", 
          details: error.errors.map(e => ({ field: e.path.join('.'), message: e.message }))
        });
      }
      // Don't expose internal errors
      throw new Error("Submission failed");
    }
  }));



  // Admin Authentication Routes
  app.post("/api/admin/auth/login", asyncHandler(async (req, res) => {
    try {
      const { email, password } = req.body;

      if (!email || !password) {
        return res.status(400).json({ error: "Email and password required" });
      }

      const admin = await storage.getAdminUserByEmail(email);
      if (!admin) {
        return res.status(401).json({ error: "Invalid credentials" });
      }

      const isValidPassword = await verifyPassword(password, admin.passwordHash);
      if (!isValidPassword) {
        return res.status(401).json({ error: "Invalid credentials" });
      }

      // Update last login
      await storage.updateAdminUserLastLogin(admin.id);

      const token = generateToken({
        id: admin.id,
        email: admin.email,
        name: admin.name,
        role: admin.role
      });

      res.json({
        token,
        user: {
          id: admin.id,
          email: admin.email,
          name: admin.name,
          role: admin.role
        }
      });
    } catch (error) {
      throw new AuthenticationError("Login failed");
    }
  }));

  // Dashboard analytics - INLINE AUTH CHECK
  app.get("/api/admin/dashboard/stats", async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;

      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }

      const token = authHeader.substring(7);
      try {
        const decoded = jwt.verify(token, JWT_SECRET) as any;
        // Token verified successfully
        req.user = decoded;
      } catch (jwtError) {
        // JWT verification failed
        return res.status(401).json({ message: "Invalid token" });
      }

      // Fetch stats
      const stats = await storage.getDashboardStats();

      const topPages = await storage.getTopPages(10);
      const deviceStats = await storage.getDeviceStats();
      const countryStats = await storage.getCountryStats();

      res.json({
        ...stats,
        topPages,
        deviceStats,
        countryStats
      });
    } catch (error) {
      // Error fetching dashboard stats
      res.status(500).json({ error: "Failed to fetch stats" });
    }
  });

  // Contact submissions management - INLINE AUTH CHECK
  app.get("/api/admin/dashboard/contacts", async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        const decoded = jwt.verify(token, JWT_SECRET) as any;
        req.user = decoded;
      } catch (jwtError) {
        return res.status(401).json({ message: "Invalid token" });
      }

      const page = parseInt(req.query.page as string) || 1;
      const limit = parseInt(req.query.limit as string) || 50;
      const offset = (page - 1) * limit;

      const contacts = await storage.getContactSubmissions(limit, offset);
      const total = await storage.getContactSubmissionsCount();

      res.json({
        contacts,
        pagination: {
          page,
          limit,
          total,
          pages: Math.ceil(total / limit)
        }
      });
    } catch (error) {
      console.error("Error fetching contacts:", error);
      res.status(500).json({ error: "Failed to fetch contacts" });
    }
  });

  app.patch("/api/admin/dashboard/contacts/:id", async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        jwt.verify(token, JWT_SECRET);
      } catch {
        return res.status(401).json({ message: "Invalid token" });
      }

      const id = parseInt(req.params.id);
      const { status } = req.body;

      if (!['new', 'contacted', 'qualified', 'closed'].includes(status)) {
        return res.status(400).json({ error: "Invalid status" });
      }

      const updated = await storage.updateContactSubmissionStatus(id, status);
      res.json(updated);
    } catch (error) {
      console.error("Error updating contact:", error);
      res.status(500).json({ error: "Failed to update contact" });
    }
  });

  // Email campaigns - INLINE AUTH CHECK
  app.get("/api/admin/dashboard/campaigns", async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        jwt.verify(token, JWT_SECRET);
      } catch {
        return res.status(401).json({ message: "Invalid token" });
      }

      const campaigns = await storage.getCampaigns();
      res.json(campaigns);
    } catch (error) {
      console.error("Error fetching campaigns:", error);
      res.status(500).json({ error: "Failed to fetch campaigns" });
    }
  });

  // Create email campaign - INLINE AUTH CHECK
  app.post("/api/admin/dashboard/campaigns", async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        jwt.verify(token, JWT_SECRET);
      } catch {
        return res.status(401).json({ message: "Invalid token" });
      }

      // Map frontend data to backend schema
      const { name, subject, content, scheduledDate } = req.body;
      console.log('Received campaign data:', { name, subject, content, scheduledDate });

      const campaignData = {
        name,
        subject,
        content,
        scheduledAt: scheduledDate && scheduledDate !== '' ? scheduledDate : undefined
      };

      console.log('Mapped campaign data:', campaignData);
      const validatedData = insertEmailCampaignSchema.parse(campaignData);
      console.log('Validated campaign data:', validatedData);

      const campaign = await storage.createCampaign(validatedData);
      res.json(campaign);
    } catch (error: any) {
      console.error("Error creating campaign:", error);
      if (error.issues) {
        console.error("Zod validation errors:", error.issues);
      }
      res.status(400).json({ error: "Invalid campaign data", details: error.message });
    }
  });

  // Update email campaign - INLINE AUTH CHECK
  app.patch("/api/admin/dashboard/campaigns/:id", async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        jwt.verify(token, JWT_SECRET);
      } catch {
        return res.status(401).json({ message: "Invalid token" });
      }

      const id = parseInt(req.params.id);
      const updates = req.body;
      const campaign = await storage.updateCampaign(id, updates);
      res.json(campaign);
    } catch (error: any) {
      console.error("Error updating campaign:", error);
      res.status(500).json({ error: "Failed to update campaign" });
    }
  });

  // Delete email campaign - INLINE AUTH CHECK
  app.delete("/api/admin/dashboard/campaigns/:id", async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        jwt.verify(token, JWT_SECRET);
      } catch {
        return res.status(401).json({ message: "Invalid token" });
      }

      const id = parseInt(req.params.id);
      await storage.deleteCampaign(id);
      res.json({ success: true });
    } catch (error: any) {
      console.error("Error deleting campaign:", error);
      res.status(500).json({ error: "Failed to delete campaign" });
    }
  });

  // Page builder - Get all pages - INLINE AUTH CHECK
  app.get("/api/admin/pages", async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        jwt.verify(token, JWT_SECRET);
      } catch {
        return res.status(401).json({ message: "Invalid token" });
      }

      const { status } = req.query;
      const pages = await storage.getPages(status as string);
      res.json(pages || []);
    } catch (error) {
      console.error("Error fetching pages:", error);
      res.status(500).json({ error: "Failed to fetch pages" });
    }
  });

  // Content management - INLINE AUTH CHECK
  app.get("/api/admin/dashboard/content", async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        jwt.verify(token, JWT_SECRET);
      } catch {
        return res.status(401).json({ message: "Invalid token" });
      }

      const page = req.query.page as string;
      const content = await storage.getContentBlocks(page);
      res.json(content);
    } catch (error) {
      console.error("Error fetching content:", error);
      res.status(500).json({ error: "Failed to fetch content" });
    }
  });

  // User Management - INLINE AUTH CHECK
  app.get("/api/admin/users", async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        jwt.verify(token, JWT_SECRET);
      } catch {
        return res.status(401).json({ message: "Invalid token" });
      }

      const users = await storage.getAdminUsers();
      // Remove password hashes from response
      const sanitizedUsers = users.map(user => {
        const { passwordHash, ...userWithoutPassword } = user;
        return userWithoutPassword;
      });
      res.json(sanitizedUsers);
    } catch (error) {
      console.error("Error fetching users:", error);
      res.status(500).json({ error: "Failed to fetch users" });
    }
  });

  app.post("/api/admin/users", async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        jwt.verify(token, JWT_SECRET);
      } catch {
        return res.status(401).json({ message: "Invalid token" });
      }

      const { email, name, role, password } = req.body;
      const hashedPassword = await bcrypt.hash(password, 10);

      const newUser = await storage.createAdminUser({
        email,
        name,
        role,
        passwordHash: hashedPassword
      });

      const { passwordHash, ...userResponse } = newUser;
      res.json(userResponse);
    } catch (error) {
      console.error("Error creating user:", error);
      res.status(500).json({ error: "Failed to create user" });
    }
  });

  // Integrations Management - INLINE AUTH CHECK
  app.get("/api/admin/integrations", async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        jwt.verify(token, JWT_SECRET);
      } catch {
        return res.status(401).json({ message: "Invalid token" });
      }

      const integrations = await storage.getIntegrations();
      res.json(integrations);
    } catch (error) {
      console.error("Error fetching integrations:", error);
      res.status(500).json({ error: "Failed to fetch integrations" });
    }
  });

  // Dashboard Widgets and Customization - INLINE AUTH CHECK
  app.get("/api/admin/dashboard/widgets", async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        jwt.verify(token, JWT_SECRET);
      } catch {
        return res.status(401).json({ message: "Invalid token" });
      }

      const widgets = await storage.getDashboardWidgets();
      res.json(widgets);
    } catch (error) {
      console.error("Error fetching dashboard widgets:", error);
      res.status(500).json({ error: "Failed to fetch dashboard widgets" });
    }
  });

  app.get("/api/admin/user-dashboards", async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        const decoded = jwt.verify(token, JWT_SECRET) as any;
        const dashboards = await storage.getUserDashboards(decoded.id);
        res.json(dashboards);
      } catch {
        return res.status(401).json({ message: "Invalid token" });
      }
    } catch (error) {
      console.error("Error fetching user dashboards:", error);
      res.status(500).json({ error: "Failed to fetch user dashboards" });
    }
  });

  app.post("/api/admin/user-dashboards", async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        const decoded = jwt.verify(token, JWT_SECRET) as any;
        const { name, layout } = req.body;
        const dashboard = await storage.createUserDashboard({
          adminUserId: decoded.id,
          name,
          layout: layout || [],
          isDefault: false
        });
        res.json(dashboard);
      } catch {
        return res.status(401).json({ message: "Invalid token" });
      }
    } catch (error) {
      console.error("Error creating user dashboard:", error);
      res.status(500).json({ error: "Failed to create user dashboard" });
    }
  });

  // Pages management endpoints
  app.get('/api/admin/pages', async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        jwt.verify(token, JWT_SECRET);
      } catch {
        return res.status(401).json({ message: "Invalid token" });
      }

      const status = req.query.status as string;
      const pages = await storage.getPages(status);
      res.json(pages);
    } catch (error) {
      console.error('Error fetching pages:', error);
      res.status(500).json({ message: 'Failed to fetch pages' });
    }
  });

  app.get('/api/admin/pages/:slug/edit', async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        jwt.verify(token, JWT_SECRET);
      } catch {
        return res.status(401).json({ message: "Invalid token" });
      }

      const slug = req.params.slug;
      const page = await storage.getPageBySlug(slug);
      if (!page) {
        return res.status(404).json({ message: 'Page not found' });
      }

      const components = await storage.getPageComponents(page.id);
      res.json({ page, components });
    } catch (error) {
      console.error('Error fetching page for editing:', error);
      res.status(500).json({ message: 'Failed to fetch page' });
    }
  });

  app.get('/api/admin/pages/:id/components', async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        jwt.verify(token, JWT_SECRET);
      } catch {
        return res.status(401).json({ message: "Invalid token" });
      }

      const pageId = parseInt(req.params.id);
      const components = await storage.getPageComponents(pageId);
      res.json(components);
    } catch (error) {
      console.error('Error fetching page components:', error);
      res.status(500).json({ message: 'Failed to fetch page components' });
    }
  });

  app.post('/api/admin/pages', async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        jwt.verify(token, JWT_SECRET);
      } catch {
        return res.status(401).json({ message: "Invalid token" });
      }

      const pageData = req.body;
      const newPage = await storage.createPage(pageData);
      res.status(201).json(newPage);
    } catch (error) {
      console.error('Error creating page:', error);
      res.status(500).json({ message: 'Failed to create page' });
    }
  });

  app.delete('/api/admin/pages/:id', async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        jwt.verify(token, JWT_SECRET);
      } catch {
        return res.status(401).json({ message: "Invalid token" });
      }

      const pageId = parseInt(req.params.id);
      await storage.deletePage(pageId);
      res.json({ message: 'Page deleted successfully' });
    } catch (error) {
      console.error('Error deleting page:', error);
      res.status(500).json({ message: 'Failed to delete page' });
    }
  });

  // Import existing pages endpoint
  app.post('/api/admin/import-pages', async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        jwt.verify(token, JWT_SECRET);
      } catch {
        return res.status(401).json({ message: "Invalid token" });
      }

      // Import existing website pages into the page builder
      const { importExistingPages } = await import('../scripts/import-existing-pages');
      await importExistingPages();

      res.json({ message: 'Successfully imported existing pages' });
    } catch (error) {
      console.error('Error importing pages:', error);
      res.status(500).json({ message: 'Failed to import pages' });
    }
  });

  // Get page components
  app.get('/api/admin/pages/:id/components', async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        jwt.verify(token, JWT_SECRET);
      } catch {
        return res.status(401).json({ message: "Invalid token" });
      }

      const pageId = parseInt(req.params.id);
      const components = await storage.getPageComponents(pageId);
      res.json(components);
    } catch (error) {
      console.error('Error fetching page components:', error);
      res.status(500).json({ message: 'Failed to fetch page components' });
    }
  });

  // Dashboard customization endpoints - removed duplicate route

  app.get("/api/admin/dashboards/:id/widgets", async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        jwt.verify(token, JWT_SECRET);
      } catch {
        return res.status(401).json({ message: "Invalid token" });
      }

      const dashboardId = parseInt(req.params.id);
      const widgets = await storage.getDashboardWidgetInstances(dashboardId);
      res.json(widgets);
    } catch (error) {
      console.error("Error fetching dashboard widgets:", error);
      res.status(500).json({ error: "Failed to fetch dashboard widgets" });
    }
  });

  app.post("/api/admin/dashboards/:id/widgets", async (req: AuthenticatedRequest, res) => {
    try {
      // Inline authentication check
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ message: "No token provided" });
      }
      const token = authHeader.substring(7);
      try {
        jwt.verify(token, JWT_SECRET);
      } catch {
        return res.status(401).json({ message: "Invalid token" });
      }

      const dashboardId = parseInt(req.params.id);
      const { widgetId, title, position, config } = req.body;

      const widgetInstance = await storage.createDashboardWidgetInstance({
        dashboardId,
        widgetId,
        title: title || null,
        position: position || { x: 0, y: 0, w: 2, h: 2 },
        config: config || {},
        isVisible: true,
        refreshInterval: 300
      });

      res.json(widgetInstance);
    } catch (error) {
      console.error("Error adding widget to dashboard:", error);
      res.status(500).json({ error: "Failed to add widget" });
    }
  });

  // Health check endpoint
  app.get('/health', (req, res) => {
    res.json({ status: 'ok', timestamp: new Date().toISOString() });
  });

  // Contact form submission
  app.post('/api/contact', async (req, res) => {
    try {
      const { name, email, company, phone, message, source } = req.body;

      if (!name || !email || !message) {
        return res.status(400).json({ 
          message: 'Name, email, and message are required fields' 
        });
      }

      const contactSubmission = await storage.createContactSubmission({
        name,
        email,
        company: company || null,
        phone: phone || null,
        message,
        source: source || 'contact_form'
      });

      // Send email notifications
      try {
        await emailService.sendContactFormEmail({
          name,
          email,
          company: company || undefined,
          phone: phone || undefined,
          message,
          source: source || 'contact_form'
        });

        // Send auto-reply to the user
        await emailService.sendAutoReply({
          name,
          email,
          company: company || undefined,
          phone: phone || undefined,
          message,
          source: source || 'contact_form'
        });
      } catch (emailError) {
        console.error('Email sending failed:', emailError);
        // Continue with success response even if email fails
      }

      // Track analytics
      console.log('Contact form submission:', {
        contactId: contactSubmission.id,
        source,
        timestamp: new Date().toISOString()
      });

      res.status(201).json({ 
        message: 'Contact submission received successfully',
        id: contactSubmission.id 
      });
    } catch (error) {
      console.error('Error processing contact submission:', error);
      res.status(500).json({ 
        message: 'Failed to process contact submission' 
      });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { db } from "./db";
import { 
  contactSubmissions, 
  pageViews, 
  userSessions,
  adminUsers,
  emailCampaigns,
  contentBlocks,
  systemLogs,
  userActivities,
  pages,
  pageComponents,
  pageTemplates,
  dashboardWidgets,
  userDashboards,
  dashboardWidgetInstances,
  type ContactSubmission,
  type InsertContactSubmission,
  type PageView,
  type InsertPageView,

  type UserSession,
  type AdminUser,
  type InsertAdminUser,
  type EmailCampaign,
  type InsertEmailCampaign,
  type ContentBlock,
  type InsertContentBlock,
  type SystemLog,
  type UserActivity,
  type InsertUserActivity,
  type Page,
  type InsertPage,
  type PageComponent,
  type InsertPageComponent,
  type PageTemplate,
  type InsertPageTemplate,
  type DashboardWidget,
  type InsertDashboardWidget,
  type UserDashboard,
  type InsertUserDashboard,
  type DashboardWidgetInstance,
  type InsertDashboardWidgetInstance
} from "@shared/schema";
import { eq, desc, asc, count, and, sql } from "drizzle-orm";

export interface IStorage {
  // Contact submissions
  createContactSubmission(submission: InsertContactSubmission): Promise<ContactSubmission>;
  getContactSubmissions(limit: number, offset: number): Promise<ContactSubmission[]>;
  getContactSubmissionsCount(): Promise<number>;
  updateContactSubmissionStatus(id: number, status: string): Promise<ContactSubmission>;

  // Analytics
  createPageView(pageView: InsertPageView): Promise<PageView>;

  createUserSession(session: any): Promise<UserSession>;

  // Dashboard stats
  getDashboardStats(): Promise<{
    totalPageViews: number;
    totalContacts: number;
    totalSessions: number;
  }>;
  getTopPages(limit: number): Promise<Array<{ path: string; count: number }>>;
  getDeviceStats(): Promise<Array<{ device: string; count: number }>>;
  getCountryStats(): Promise<Array<{ country: string; count: number }>>;

  // Admin users
  createAdminUser(user: InsertAdminUser): Promise<AdminUser>;
  getAdminUserByEmail(email: string): Promise<AdminUser | undefined>;
  updateAdminUserLastLogin(id: number): Promise<void>;
  getAdminUsers(): Promise<AdminUser[]>;
  updateAdminUser(id: number, updates: any): Promise<AdminUser>;
  deleteAdminUser(id: number): Promise<void>;

  // Integrations
  getIntegrations(): Promise<any[]>;
  createIntegration(integration: any): Promise<any>;
  updateIntegration(id: number, updates: any): Promise<any>;
  deleteIntegration(id: number): Promise<void>;

  // Dashboard widgets
  getDashboardWidgets(): Promise<DashboardWidget[]>;
  getUserDashboards(adminUserId: number): Promise<UserDashboard[]>;
  createUserDashboard(dashboard: InsertUserDashboard): Promise<UserDashboard>;

  // Pages
  getPages(status?: string): Promise<Page[]>;
  getPageBySlug(slug: string): Promise<Page | undefined>;
  createPage(pageData: InsertPage): Promise<Page>;
  updatePage(id: number, updates: any): Promise<Page>;
  deletePage(id: number): Promise<void>;
  createPageComponent(componentData: InsertPageComponent): Promise<PageComponent>;

  // Content management
  getContacts(page: number, limit: number): Promise<ContactSubmission[]>;
  updateContact(id: number, updates: any): Promise<ContactSubmission>;
  getCampaigns(): Promise<EmailCampaign[]>;
  createCampaign(campaignData: InsertEmailCampaign): Promise<EmailCampaign>;
  updateCampaign(id: number, updates: any): Promise<EmailCampaign>;
  deleteCampaign(id: number): Promise<void>;
  getContentBlocks(page?: string): Promise<ContentBlock[]>;
  createContentBlock(blockData: InsertContentBlock): Promise<ContentBlock>;
  updateContentBlock(id: number, updates: any): Promise<ContentBlock>;
  deleteContentBlock(id: number): Promise<void>;
}

export class DatabaseStorage implements IStorage {
  // Contact submissions
  async createContactSubmission(submission: InsertContactSubmission): Promise<ContactSubmission> {
    const [contact] = await db
      .insert(contactSubmissions)
      .values(submission)
      .returning();
    return contact;
  }

  async getContactSubmissions(limit: number, offset: number): Promise<ContactSubmission[]> {
    return await db
      .select()
      .from(contactSubmissions)
      .orderBy(desc(contactSubmissions.createdAt))
      .limit(limit)
      .offset(offset);
  }

  async getContactSubmissionsCount(): Promise<number> {
    const [result] = await db.select({ count: count() }).from(contactSubmissions);
    return result.count;
  }

  async updateContactSubmissionStatus(id: number, status: string): Promise<ContactSubmission> {
    const [updated] = await db
      .update(contactSubmissions)
      .set({ status, updatedAt: new Date() })
      .where(eq(contactSubmissions.id, id))
      .returning();
    return updated;
  }

  // Analytics
  async createPageView(pageView: InsertPageView): Promise<PageView> {
    const [view] = await db
      .insert(pageViews)
      .values(pageView)
      .returning();
    return view;
  }



  async createUserSession(session: any): Promise<UserSession> {
    const [userSession] = await db
      .insert(userSessions)
      .values(session)
      .returning();
    return userSession;
  }

  // Dashboard stats
  async getDashboardStats(): Promise<{
    totalPageViews: number;
    totalContacts: number;
    totalSessions: number;
  }> {
    const [pageViewCount] = await db.select({ count: count() }).from(pageViews);
    const [contactCount] = await db.select({ count: count() }).from(contactSubmissions);
    const [sessionCount] = await db.select({ count: count() }).from(userSessions);
    return {
      totalPageViews: pageViewCount.count,
      totalContacts: contactCount.count,
      totalSessions: sessionCount.count
    };
  }



  async getTopPages(limit: number): Promise<Array<{ path: string; count: number }>> {
    const results = await db
      .select({
        path: pageViews.path,
        count: count()
      })
      .from(pageViews)
      .groupBy(pageViews.path)
      .orderBy(desc(count()))
      .limit(limit);

    return results.map(r => ({ path: r.path, count: r.count }));
  }

  async getDeviceStats(): Promise<Array<{ device: string; count: number }>> {
    const results = await db
      .select({
        device: pageViews.device,
        count: count()
      })
      .from(pageViews)
      .groupBy(pageViews.device)
      .orderBy(desc(count()));

    return results.map(r => ({ device: r.device || 'unknown', count: r.count }));
  }

  async getCountryStats(): Promise<Array<{ country: string; count: number }>> {
    // For now return empty array, can be enhanced with IP geolocation
    return [];
  }

  // Admin users
  async createAdminUser(user: InsertAdminUser): Promise<AdminUser> {
    const [admin] = await db
      .insert(adminUsers)
      .values(user)
      .returning();
    return admin;
  }

  async getAdminUserByEmail(email: string): Promise<AdminUser | undefined> {
    const [admin] = await db
      .select()
      .from(adminUsers)
      .where(eq(adminUsers.email, email));
    return admin;
  }

  async updateAdminUserLastLogin(id: number): Promise<void> {
    await db
      .update(adminUsers)
      .set({ lastLogin: new Date(), updatedAt: new Date() })
      .where(eq(adminUsers.id, id));
  }

  // Content management - Admin functions
  async getContacts(page: number, limit: number): Promise<ContactSubmission[]> {
    const offset = (page - 1) * limit;
    return await db
      .select()
      .from(contactSubmissions)
      .orderBy(desc(contactSubmissions.createdAt))
      .limit(limit)
      .offset(offset);
  }

  async updateContact(id: number, updates: any): Promise<ContactSubmission> {
    const [contact] = await db
      .update(contactSubmissions)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(contactSubmissions.id, id))
      .returning();
    return contact;
  }

  async getCampaigns(): Promise<EmailCampaign[]> {
    return await db
      .select()
      .from(emailCampaigns)
      .orderBy(desc(emailCampaigns.createdAt));
  }

  async createCampaign(campaignData: InsertEmailCampaign): Promise<EmailCampaign> {
    const [campaign] = await db
      .insert(emailCampaigns)
      .values(campaignData)
      .returning();
    return campaign;
  }

  async updateCampaign(id: number, updates: any): Promise<EmailCampaign> {
    const [campaign] = await db
      .update(emailCampaigns)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(emailCampaigns.id, id))
      .returning();
    return campaign;
  }

  async deleteCampaign(id: number): Promise<void> {
    await db.delete(emailCampaigns).where(eq(emailCampaigns.id, id));
  }

  // Page management methods (consolidated with Pages implementation section)

  async getContentBlocks(page?: string): Promise<ContentBlock[]> {
    if (page) {
      return await db
        .select()
        .from(contentBlocks)
        .where(eq(contentBlocks.page, page))
        .orderBy(contentBlocks.id);
    }
    return await db
      .select()
      .from(contentBlocks)
      .orderBy(contentBlocks.page, contentBlocks.id);
  }

  async createContentBlock(blockData: InsertContentBlock): Promise<ContentBlock> {
    const [block] = await db
      .insert(contentBlocks)
      .values(blockData)
      .returning();
    return block;
  }

  async updateContentBlock(id: number, updates: any): Promise<ContentBlock> {
    const [block] = await db
      .update(contentBlocks)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(contentBlocks.id, id))
      .returning();
    return block;
  }

  async deleteContentBlock(id: number): Promise<void> {
    await db.delete(contentBlocks).where(eq(contentBlocks.id, id));
  }

  // Admin users implementation
  async getAdminUsers(): Promise<AdminUser[]> {
    try {
      console.log('Attempting to fetch admin users...');
      const result = await db
        .select()
        .from(adminUsers)
        .orderBy(desc(adminUsers.createdAt));
      console.log('Successfully fetched admin users:', result.length);
      return result;
    } catch (error) {
      console.error('Error in getAdminUsers:', error);
      throw error;
    }
  }

  async updateAdminUser(id: number, updates: any): Promise<AdminUser> {
    const [user] = await db
      .update(adminUsers)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(adminUsers.id, id))
      .returning();
    return user;
  }

  async deleteAdminUser(id: number): Promise<void> {
    await db.delete(adminUsers).where(eq(adminUsers.id, id));
  }

  // Integrations implementation (simple mock data for now)
  async getIntegrations(): Promise<any[]> {
    // Return mock integration data since we don't have an integrations table
    return [
      {
        id: 1,
        name: "Zapier Integration",
        type: "zapier",
        isActive: false,
        webhookUrl: "",
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString()
      },
      {
        id: 2,
        name: "Brevo Email Integration",
        type: "brevo",
        isActive: false,
        apiKey: "",
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString()
      }
    ];
  }

  async createIntegration(integration: any): Promise<any> {
    // Mock implementation - in real app would save to database
    return {
      id: Date.now(),
      ...integration,
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString()
    };
  }

  async updateIntegration(id: number, updates: any): Promise<any> {
    // Mock implementation
    return {
      id,
      ...updates,
      updatedAt: new Date().toISOString()
    };
  }

  async deleteIntegration(id: number): Promise<void> {
    // Mock implementation
    console.log(`Integration ${id} deleted`);
  }

  // Dashboard widgets implementation
  async getDashboardWidgets(): Promise<DashboardWidget[]> {
    try {
      console.log('Attempting to fetch dashboard widgets...');
      const result = await db
        .select()
        .from(dashboardWidgets)
        .orderBy(dashboardWidgets.name);
      console.log('Successfully fetched dashboard widgets:', result.length);
      return result;
    } catch (error) {
      console.error('Error in getDashboardWidgets:', error);
      throw error;
    }
  }

  async getUserDashboards(adminUserId: number): Promise<UserDashboard[]> {
    return await db
      .select()
      .from(userDashboards)
      .where(eq(userDashboards.adminUserId, adminUserId))
      .orderBy(desc(userDashboards.isDefault), userDashboards.name);
  }

  async createUserDashboard(dashboard: InsertUserDashboard): Promise<UserDashboard> {
    const [newDashboard] = await db
      .insert(userDashboards)
      .values(dashboard)
      .returning();
    return newDashboard;
  }

  // Page components methods
  async getPageComponents(pageId?: number): Promise<ContentBlock[]> {
    try {
      if (pageId) {
        return await db
          .select()
          .from(contentBlocks)
          .where(eq(contentBlocks.page, pageId.toString()))
          .orderBy(contentBlocks.id);
      }
      return await db
        .select()
        .from(contentBlocks)
        .orderBy(contentBlocks.page, contentBlocks.id);
    } catch (error) {
      console.error("Error fetching page components:", error);
      return [];
    }
  }

  // Pages implementation
  async getPages(status?: string): Promise<Page[]> {
    try {
      if (status) {
        return await db
          .select()
          .from(pages)
          .where(eq(pages.status, status))
          .orderBy(desc(pages.createdAt));
      }
      return await db
        .select()
        .from(pages)
        .orderBy(desc(pages.createdAt));
    } catch (error) {
      console.error("Error fetching pages:", error);
      return [];
    }
  }

  async getPageBySlug(slug: string): Promise<Page | undefined> {
    try {
      const [page] = await db
        .select()
        .from(pages)
        .where(eq(pages.slug, slug))
        .limit(1);
      return page;
    } catch (error) {
      console.error("Error fetching page by slug:", error);
      return undefined;
    }
  }

  async createPage(pageData: InsertPage): Promise<Page> {
    const [newPage] = await db
      .insert(pages)
      .values(pageData)
      .returning();
    return newPage;
  }

  async updatePage(id: number, updates: any): Promise<Page> {
    const [updatedPage] = await db
      .update(pages)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(pages.id, id))
      .returning();
    return updatedPage;
  }

  async deletePage(id: number): Promise<void> {
    // First delete associated components
    await db.delete(pageComponents).where(eq(pageComponents.pageId, id));
    // Then delete the page
    await db.delete(pages).where(eq(pages.id, id));
  }

  async createPageComponent(componentData: InsertPageComponent): Promise<PageComponent> {
    const [newComponent] = await db
      .insert(pageComponents)
      .values(componentData)
      .returning();
    return newComponent;
  }

  // Dashboard widget instances methods
  async getDashboardWidgetInstances(dashboardId: number): Promise<any[]> {
    // Mock implementation for now - return empty array
    console.log(`Fetching widget instances for dashboard ${dashboardId}`);
    return [];
  }

  async createDashboardWidgetInstance(instance: any): Promise<any> {
    // Mock implementation for now
    console.log('Creating dashboard widget instance:', instance);
    return {
      id: Date.now(),
      ...instance,
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString()
    };
  }
}

export const storage = new DatabaseStorage();

