          <a 
            href="/industries" 
            className="bg-navy-blue text-white px-6 py-3 rounded-lg hover:bg-charcoal transition-colors"
          >
            View All Industries
          </a>
        </div>
      </div>
    );
  }

  return <IndustryComponent />;
}

