client/src/components/admin/ProfileSetti.content:
import { useState } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Separator } from '@/components/ui/separator';
import { useToast } from '@/hooks/use-toast';
import { useAuth } from '@/hooks/useAuth';
import { User, Lock, Save } from 'lucide-react';

interface ProfileSettingsProps {
  token: string | null;
}

export default function ProfileSettings({ token }: ProfileSettingsProps) {
  const { user } = useAuth();
  const { toast } = useToast();
  const [isUpdatingProfile, setIsUpdatingProfile] = useState(false);
  const [isChangingPassword, setIsChangingPassword] = useState(false);

  const [profileData, setProfileData] = useState({
    name: user?.name || '',
    email: user?.email || '',
  });

  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: '',
  });

  const handleProfileUpdate = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsUpdatingProfile(true);

    try {
      const response = await fetch('/api/admin/profile', {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(profileData),
      });

      if (response.ok) {
        toast({
          title: 'Success',
          description: 'Profile updated successfully',
        });
      } else {
        const error = await response.json();
        toast({
          title: 'Error',
          description: error.message || 'Failed to update profile',
          variant: 'destructive',
        });
      }
    } catch (error) {
      toast({
        title: 'Error',
        description: 'An error occurred while updating profile',
        variant: 'destructive',
      });
    } finally {
      setIsUpdatingProfile(false);
    }
  };

  const handlePasswordChange = async (e: React.FormEvent) => {
    e.preventDefault();

    if (passwordData.newPassword !== passwordData.confirmPassword) {
      toast({
        title: 'Error',
        description: 'New passwords do not match',
        variant: 'destructive',
      });
      return;
    }

    if (passwordData.newPassword.length < 6) {
      toast({
        title: 'Error',
        description: 'Password must be at least 6 characters long',
        variant: 'destructive',
      });
      return;
    }

    setIsChangingPassword(true);

    try {
      const response = await fetch('/api/admin/change-password', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          currentPassword: passwordData.currentPassword,
          newPassword: passwordData.newPassword,
        }),
      });

      if (response.ok) {
        toast({
          title: 'Success',
          description: 'Password changed successfully',
        });
        setPasswordData({
          currentPassword: '',
          newPassword: '',
          confirmPassword: '',
        });
      } else {
        const error = await response.json();
        toast({
          title: 'Error',
          description: error.message || 'Failed to change password',
          variant: 'destructive',
        });
      }
    } catch (error) {
      toast({
        title: 'Error',
        description: 'An error occurred while changing password',
        variant: 'destructive',
      });
    } finally {
      setIsChangingPassword(false);
    }
  };

  return (
    <div className="space-y-6">
      {/* Profile Information */}
      <Card>
        <CardHeader>
          <div className="flex items-center gap-2">
            <User className="h-5 w-5 text-navy-blue" />
            <div>
              <CardTitle>Profile Information</CardTitle>
              <CardDescription>
                Update your personal information and account details
              </CardDescription>
            </div>
          </div>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleProfileUpdate} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="name">Full Name</Label>
                <Input
                  id="name"
                  value={profileData.name}
                  onChange={(e) => setProfileData({...profileData, name: e.target.value})}
                  placeholder="Enter your full name"
                  required
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="email">Email Address</Label>
                <Input
                  id="email"
                  type="email"
                  value={profileData.email}
                  onChange={(e) => setProfileData({...profileData, email: e.target.value})}
                  placeholder="Enter your email"
                  required
                  disabled
                />
                <p className="text-xs text-charcoal">Email cannot be changed for security reasons</p>
              </div>
            </div>
            <div className="space-y-2">
              <Label>Role</Label>
              <div className="px-3 py-2 bg-soft-beige rounded-md">
                <span className="text-sm font-medium text-navy-blue">
                  {user?.role?.replace('_', ' ')}
                </span>
              </div>
            </div>
            <div className="flex justify-end">
              <Button 
                type="submit" 
                disabled={isUpdatingProfile}
                className="bg-navy-blue hover:bg-navy-blue/90"
              >
                <Save className="h-4 w-4 mr-2" />
                {isUpdatingProfile ? 'Updating...' : 'Update Profile'}
              </Button>
            </div>
          </form>
        </CardContent>
      </Card>

      {/* Change Password */}
      <Card>
        <CardHeader>
          <div className="flex items-center gap-2">
            <Lock className="h-5 w-5 text-navy-blue" />
            <div>
              <CardTitle>Change Password</CardTitle>
              <CardDescription>
                Update your password to keep your account secure
              </CardDescription>
            </div>
          </div>
        </CardHeader>
        <CardContent>
          <form onSubmit={handlePasswordChange} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="currentPassword">Current Password</Label>
              <Input
                id="currentPassword"
                type="password"
                value={passwordData.currentPassword}
                onChange={(e) => setPasswordData({...passwordData, currentPassword: e.target.value})}
                placeholder="Enter your current password"
                required
              />
            </div>
            <Separator />
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="newPassword">New Password</Label>
                <Input
                  id="newPassword"
                  type="password"
                  value={passwordData.newPassword}
                  onChange={(e) => setPasswordData({...passwordData, newPassword: e.target.value})}
                  placeholder="Enter new password"
                  required
                  minLength={6}
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="confirmPassword">Confirm New Password</Label>
                <Input
                  id="confirmPassword"
                  type="password"
                  value={passwordData.confirmPassword}
                  onChange={(e) => setPasswordData({...passwordData, confirmPassword: e.target.value})}
                  placeholder="Confirm new password"
                  required
                  minLength={6}
                />
              </div>
            </div>
            <div className="space-y-2">
              <p className="text-xs text-charcoal">
                Password must be at least 6 characters long and contain a mix of letters and numbers for security.
              </p>
            </div>
            <div className="flex justify-end">
              <Button 
                type="submit" 
                disabled={isChangingPassword}
                className="bg-warm-orange hover:bg-warm-orange/90"
              >
                <Lock className="h-4 w-4 mr-2" />
                {isChangingPassword ? 'Changing...' : 'Change Password'}
              </Button>
            </div>
          </form>
        </CardContent>
      </Card>

      {/* Account Information */}
      <Card>
        <CardHeader>
          <CardTitle>Account Information</CardTitle>
          <CardDescription>
            View your account details and activity
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="space-y-3">
              <div>
                <Label className="text-sm font-medium text-charcoal">Account Created</Label>
                <p className="text-sm text-navy-blue">
                  {user?.createdAt ? new Date(user.createdAt).toLocaleDateString() : 'Unknown'}
                </p>
              </div>
              <div>
                <Label className="text-sm font-medium text-charcoal">Last Login</Label>
                <p className="text-sm text-navy-blue">
                  {user?.lastLoginAt ? new Date(user.lastLoginAt).toLocaleString() : 'Never'}
                </p>
              </div>
            </div>
            <div className="space-y-3">
              <div>
                <Label className="text-sm font-medium text-charcoal">Account Status</Label>
                <p className="text-sm text-navy-blue">Active</p>
              </div>
              <div>
                <Label className="text-sm font-medium text-charcoal">User ID</Label>
                <p className="text-sm text-navy-blue font-mono">{user?.id}</p>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/admin/UserManageme.content:
import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from '@/components/ui/dialog';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { useToast } from '@/hooks/use-toast';
import { format } from 'date-fns';
import { Plus, Edit, Trash2, UserCheck, UserX } from 'lucide-react';

interface AdminUser {
  id: number;
  email: string;
  name: string;
  role: string;
  isActive: boolean;
  lastLoginAt?: string;
  createdAt: string;
  updatedAt: string;
}

interface UserManagementProps {
  token: string | null;
}

export default function UserManagement({ token }: UserManagementProps) {
  const [users, setUsers] = useState<AdminUser[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingUser, setEditingUser] = useState<AdminUser | null>(null);
  const { toast } = useToast();

  const [formData, setFormData] = useState({
    email: '',
    name: '',
    role: 'admin',
    password: '',
  });

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await fetch('/api/admin/users', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        setUsers(data);
      } else {
        toast({
          title: 'Error',
          description: 'Failed to fetch users',
          variant: 'destructive',
        });
      }
    } catch (error) {
      toast({
        title: 'Error',
        description: 'An error occurred while fetching users',
        variant: 'destructive',
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      const url = editingUser 
        ? `/api/admin/users/${editingUser.id}`
        : '/api/admin/users';

      const method = editingUser ? 'PATCH' : 'POST';

      const payload = editingUser 
        ? { name: formData.name, role: formData.role }
        : formData;

      const response = await fetch(url, {
        method,
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      if (response.ok) {
        await fetchUsers();
        setIsDialogOpen(false);
        setEditingUser(null);
        setFormData({ email: '', name: '', role: 'admin', password: '' });
        toast({
          title: 'Success',
          description: `User ${editingUser ? 'updated' : 'created'} successfully`,
        });
      } else {
        const error = await response.json();
        toast({
          title: 'Error',
          description: error.message || `Failed to ${editingUser ? 'update' : 'create'} user`,
          variant: 'destructive',
        });
      }
    } catch (error) {
      toast({
        title: 'Error',
        description: 'An error occurred while saving user',
        variant: 'destructive',
      });
    }
  };

  const toggleUserStatus = async (userId: number, currentStatus: boolean) => {
    try {
      const response = await fetch(`/api/admin/users/${userId}`, {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ isActive: !currentStatus }),
      });

      if (response.ok) {
        await fetchUsers();
        toast({
          title: 'Success',
          description: `User ${!currentStatus ? 'activated' : 'deactivated'} successfully`,
        });
      } else {
        throw new Error('Failed to update user status');
      }
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to update user status',
        variant: 'destructive',
      });
    }
  };

  const deleteUser = async (userId: number) => {
    if (!confirm('Are you sure you want to delete this user? This action cannot be undone.')) {
      return;
    }

    try {
      const response = await fetch(`/api/admin/users/${userId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (response.ok) {
        setUsers(users.filter(u => u.id !== userId));
        toast({
          title: 'Success',
          description: 'User deleted successfully',
        });
      } else {
        throw new Error('Failed to delete user');
      }
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to delete user',
        variant: 'destructive',
      });
    }
  };

  const openEditDialog = (user: AdminUser) => {
    setEditingUser(user);
    setFormData({
      email: user.email,
      name: user.name,
      role: user.role,
      password: '',
    });
    setIsDialogOpen(true);
  };

  const openCreateDialog = () => {
    setEditingUser(null);
    setFormData({ email: '', name: '', role: 'admin', password: '' });
    setIsDialogOpen(true);
  };

  const getRoleBadgeVariant = (role: string) => {
    switch (role) {
      case 'super_admin': return 'default';
      case 'admin': return 'secondary';
      case 'manager': return 'outline';
      default: return 'secondary';
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center p-8">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-navy-blue"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <div className="flex justify-between items-center">
            <div>
              <CardTitle>User Management</CardTitle>
              <CardDescription>
                Manage admin users and their permissions
              </CardDescription>
            </div>
            <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
              <DialogTrigger asChild>
                <Button onClick={openCreateDialog} className="bg-navy-blue hover:bg-navy-blue/90">
                  <Plus className="h-4 w-4 mr-2" />
                  Add User
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>
                    {editingUser ? 'Edit User' : 'Create New User'}
                  </DialogTitle>
                  <DialogDescription>
                    {editingUser ? 'Update user details and permissions' : 'Add a new admin user to the system'}
                  </DialogDescription>
                </DialogHeader>
                <form onSubmit={handleSubmit} className="space-y-4">
                  <div className="space-y-2">
                    <Label htmlFor="name">Full Name</Label>
                    <Input
                      id="name"
                      value={formData.name}
                      onChange={(e) => setFormData({...formData, name: e.target.value})}
                      placeholder="Enter full name"
                      required
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="email">Email Address</Label>
                    <Input
                      id="email"
                      type="email"
                      value={formData.email}
                      onChange={(e) => setFormData({...formData, email: e.target.value})}
                      placeholder="Enter email address"
                      required
                      disabled={!!editingUser}
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="role">Role</Label>
                    <Select
                      value={formData.role}
                      onValueChange={(value) => setFormData({...formData, role: value})}
                    >
                      <SelectTrigger>
                        <SelectValue placeholder="Select role" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="admin">Admin</SelectItem>
                        <SelectItem value="manager">Manager</SelectItem>
                        <SelectItem value="super_admin">Super Admin</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                  {!editingUser && (
                    <div className="space-y-2">
                      <Label htmlFor="password">Password</Label>
                      <Input
                        id="password"
                        type="password"
                        value={formData.password}
                        onChange={(e) => setFormData({...formData, password: e.target.value})}
                        placeholder="Enter password"
                        required
                        minLength={6}
                      />
                    </div>
                  )}
                  <div className="flex justify-end gap-2">
                    <Button type="button" variant="outline" onClick={() => setIsDialogOpen(false)}>
                      Cancel
                    </Button>
                    <Button type="submit" className="bg-navy-blue hover:bg-navy-blue/90">
                      {editingUser ? 'Update' : 'Create'} User
                    </Button>
                  </div>
                </form>
              </DialogContent>
            </Dialog>
          </div>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Name</TableHead>
                <TableHead>Email</TableHead>
                <TableHead>Role</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Last Login</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {users.map((user) => (
                <TableRow key={user.id}>
                  <TableCell className="font-medium">{user.name}</TableCell>
                  <TableCell>{user.email}</TableCell>
                  <TableCell>
                    <Badge variant={getRoleBadgeVariant(user.role)}>
                      {user.role.replace('_', ' ')}
                    </Badge>
                  </TableCell>
                  <TableCell>
                    <Badge variant={user.isActive ? 'default' : 'destructive'}>
                      {user.isActive ? 'Active' : 'Inactive'}
                    </Badge>
                  </TableCell>
                  <TableCell>
                    {user.lastLoginAt 
                      ? format(new Date(user.lastLoginAt), 'MMM dd, yyyy HH:mm')
                      : 'Never'
                    }
                  </TableCell>
                  <TableCell>
                    <div className="flex items-center gap-2">
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => openEditDialog(user)}
                      >
                        <Edit className="h-4 w-4" />
                      </Button>
                      <Button
                        size="sm"
                        variant={user.isActive ? 'destructive' : 'default'}
                        onClick={() => toggleUserStatus(user.id, user.isActive)}
                      >
                        {user.isActive ? <UserX className="h-4 w-4" /> : <UserCheck className="h-4 w-4" />}
                      </Button>
                      <Button
                        size="sm"
                        variant="destructive"
                        onClick={() => deleteUser(user.id)}
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
          {users.length === 0 && (
            <div className="text-center py-8 text-charcoal">
              No users found. Create your first user to get started.
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/admin/AdminLogin.tsx.content:
import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';

import { useToast } from '@/hooks/use-toast';
import { Link } from 'wouter';

import { useAuth } from '@/hooks/useAuth';
import { useLocation } from 'wouter';

export default function AdminLogin() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();
  const { login } = useAuth();
  const [, setLocation] = useLocation();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      console.log('Attempting login with:', email);
      const success = await login(email, password);
      console.log('Login result:', success);

      if (success) {
        console.log('Login successful, redirecting to dashboard...');
        toast({
          title: 'Login Successful',
          description: 'Welcome to the admin dashboard!',
        });
        // Use a small delay to ensure the toast is shown
        setTimeout(() => {
          setLocation('/admin/dashboard');
        }, 500);
      } else {
        console.log('Login failed');
        toast({
          title: 'Login Failed',
          description: 'Invalid email or password. Please try again.',
          variant: 'destructive',
        });
      }
    } catch (error) {
      console.error('Login error:', error);
      toast({
        title: 'Error',
        description: 'An error occurred during login. Please try again.',
        variant: 'destructive',
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-soft-beige flex items-center justify-center p-4">
      <div className="w-full max-w-md">
        <Card className="shadow-lg">
          <CardHeader className="text-center">
            <CardTitle className="text-2xl font-bold text-navy-blue">
              Admin Portal
            </CardTitle>
            <CardDescription>
              Access the Asterik administration dashboard
            </CardDescription>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="email">Email</Label>
                <Input
                  id="email"
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="Enter your admin email"
                  required
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="password">Password</Label>
                <Input
                  id="password"
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Enter your password"
                  required
                />
              </div>
              <Button 
                type="submit" 
                className="w-full bg-navy-blue hover:bg-navy-blue/90 text-white"
                disabled={isLoading}
              >
                {isLoading ? 'Signing In...' : 'Sign In'}
              </Button>
            </form>
            <div className="mt-6 text-center">
              <Link href="/" className="text-sm text-charcoal hover:text-navy-blue">
                ← Back to Website
              </Link>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/admin/PageBuilder.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { Plus, Copy, Eye, Settings, Edit, Trash2 } from "lucide-react";

interface Page {
  id: number;
  title: string;
  slug: string;
  description?: string;
  status: string;
  template?: string;
  seoTitle?: string;
  seoDescription?: string;
  seoKeywords?: string;
  isPublic: boolean;
  createdAt: string;
  updatedAt: string;
}

interface PageComponent {
  id: number;
  pageId: number;
  type: string;
  name?: string;
  content: any;
  settings?: any;
  sortOrder: number;
  isVisible: boolean;
  createdAt: string;
  updatedAt: string;
}

interface PageBuilderProps {
  token?: string;
}

export default function PageBuilder({ token }: PageBuilderProps) {
  const [selectedPage, setSelectedPage] = useState<Page | null>(null);
  const [showCreateDialog, setShowCreateDialog] = useState(false);
  const [showImportDialog, setShowImportDialog] = useState(false);
  const [activeTab, setActiveTab] = useState("pages");
  const [existingPages] = useState([
    { title: "Home", slug: "home", description: "Main landing page" },
    { title: "About", slug: "about", description: "About us page" },
    { title: "Services", slug: "services", description: "Services overview" },
    { title: "Industries", slug: "industries", description: "Industries we serve" },
    { title: "Contact", slug: "contact", description: "Contact page" },
    { title: "Insights", slug: "insights", description: "Blog and insights" },
    { title: "Approach", slug: "approach", description: "Our methodology" }
  ]);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch pages
  const { data: pages = [], isLoading: pagesLoading } = useQuery({
    queryKey: ["/api/admin/pages"],
    queryFn: async () => {
      const response = await fetch('/api/admin/pages', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      if (!response.ok) throw new Error('Failed to fetch pages');
      return response.json();
    },
    enabled: !!token,
  });

  // Fetch components for selected page
  const { data: pageComponents = [], isLoading: componentsLoading } = useQuery({
    queryKey: ["/api/admin/pages/components", selectedPage?.id],
    queryFn: async () => {
      const response = await fetch(`/api/admin/pages/${selectedPage?.id}/components`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      if (!response.ok) throw new Error('Failed to fetch components');
      return response.json();
    },
    enabled: !!token && !!selectedPage?.id,
  });

  // Create page mutation
  const createPageMutation = useMutation({
    mutationFn: async (pageData: any) => {
      const response = await fetch('/api/admin/pages', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(pageData),
      });
      if (!response.ok) throw new Error('Failed to create page');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/pages"] });
      setShowCreateDialog(false);
      toast({ title: "Success", description: "Page created successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to create page", variant: "destructive" });
    },
  });

  // Import existing pages mutation
  const importPagesMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch('/api/admin/pages/import-existing', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ pages: existingPages }),
      });
      if (!response.ok) throw new Error('Failed to import pages');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/pages"] });
      setShowImportDialog(false);
      toast({ title: "Success", description: "Pages imported successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to import pages", variant: "destructive" });
    },
  });

  // Delete page mutation
  const deletePageMutation = useMutation({
    mutationFn: async (pageId: number) => {
      const response = await fetch(`/api/admin/pages/${pageId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      if (!response.ok) throw new Error('Failed to delete page');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/pages"] });
      toast({ title: "Success", description: "Page deleted successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to delete page", variant: "destructive" });
    },
  });

  const handleCreatePage = (formData: FormData) => {
    const pageData = {
      title: formData.get("title") as string,
      slug: formData.get("slug") as string,
      description: formData.get("description") as string,
      status: formData.get("status") as string,
      template: formData.get("template") as string,
      seoTitle: formData.get("seoTitle") as string,
      seoDescription: formData.get("seoDescription") as string,
      seoKeywords: formData.get("seoKeywords") as string,
    };
    createPageMutation.mutate(pageData);
  };

  const getStatusBadgeColor = (status: string) => {
    switch (status) {
      case "published": return "bg-green-100 text-green-800";
      case "draft": return "bg-yellow-100 text-yellow-800";
      case "archived": return "bg-gray-100 text-gray-800";
      default: return "bg-blue-100 text-blue-800";
    }
  };

  if (pagesLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-lg font-medium text-charcoal">Loading page builder...</div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-navy-blue">Page Builder</h1>
          <p className="text-charcoal">Create and manage dynamic pages with custom content</p>
        </div>
        <div className="flex gap-3">
          <Dialog open={showImportDialog} onOpenChange={setShowImportDialog}>
            <DialogTrigger asChild>
              <Button variant="outline">
                <Copy className="h-4 w-4 mr-2" />
                Import Existing Pages
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Import Existing Website Pages</DialogTitle>
                <DialogDescription>
                  Import your current website pages (Home, About, Services, etc.) into the page builder for editing. This will create editable versions of your existing pages.
                </DialogDescription>
              </DialogHeader>
              <div className="space-y-4">
                <div className="p-4 bg-blue-50 border border-blue-200 rounded-lg">
                  <h4 className="font-medium text-blue-900 mb-2">Pages to be imported:</h4>
                  <ul className="text-sm text-blue-800 space-y-1">
                    {existingPages.map((page) => (
                      <li key={page.slug} className="flex items-center gap-2">
                        <span className="w-2 h-2 bg-blue-500 rounded-full"></span>
                        {page.title} - {page.description}
                      </li>
                    ))}
                  </ul>
                </div>
                <div className="flex justify-end gap-2">
                  <Button variant="outline" onClick={() => setShowImportDialog(false)}>
                    Cancel
                  </Button>
                  <Button 
                    onClick={() => importPagesMutation.mutate()}
                    disabled={importPagesMutation.isPending}
                  >
                    {importPagesMutation.isPending ? "Importing..." : "Import Pages"}
                  </Button>
                </div>
              </div>
            </DialogContent>
          </Dialog>

          <Dialog open={showCreateDialog} onOpenChange={setShowCreateDialog}>
            <DialogTrigger asChild>
              <Button className="bg-navy-blue hover:bg-navy-blue/90">
                <Plus className="w-4 h-4 mr-2" />
                New Page
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-2xl">
              <DialogHeader>
                <DialogTitle className="text-navy-blue">Create New Page</DialogTitle>
                <DialogDescription>
                  Create a new page with custom content and settings
                </DialogDescription>
              </DialogHeader>
              <form onSubmit={(e) => {
                e.preventDefault();
                handleCreatePage(new FormData(e.currentTarget));
              }} className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="title">Page Title</Label>
                    <Input name="title" placeholder="Enter page title" required />
                  </div>
                  <div>
                    <Label htmlFor="slug">URL Slug</Label>
                    <Input name="slug" placeholder="page-url-slug" required />
                  </div>
                </div>

                <div>
                  <Label htmlFor="description">Description</Label>
                  <Textarea name="description" placeholder="Brief description of the page" />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="status">Status</Label>
                    <Select name="status" defaultValue="draft">
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="draft">Draft</SelectItem>
                        <SelectItem value="published">Published</SelectItem>
                        <SelectItem value="archived">Archived</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                  <div>
                    <Label htmlFor="template">Template</Label>
                    <Select name="template" defaultValue="standard">
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="standard">Standard</SelectItem>
                        <SelectItem value="landing">Landing Page</SelectItem>
                        <SelectItem value="service">Service Page</SelectItem>
                        <SelectItem value="industry">Industry Page</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                <div className="space-y-4">
                  <h4 className="font-medium text-navy-blue">SEO Settings</h4>
                  <div>
                    <Label htmlFor="seoTitle">SEO Title</Label>
                    <Input name="seoTitle" placeholder="SEO optimized title" />
                  </div>
                  <div>
                    <Label htmlFor="seoDescription">SEO Description</Label>
                    <Textarea name="seoDescription" placeholder="Meta description for search engines" />
                  </div>
                  <div>
                    <Label htmlFor="seoKeywords">SEO Keywords</Label>
                    <Input name="seoKeywords" placeholder="comma, separated, keywords" />
                  </div>
                </div>

                <div className="flex justify-end gap-2">
                  <Button type="button" variant="outline" onClick={() => setShowCreateDialog(false)}>
                    Cancel
                  </Button>
                  <Button 
                    type="submit" 
                    disabled={createPageMutation.isPending}
                    className="bg-navy-blue hover:bg-navy-blue/90"
                  >
                    {createPageMutation.isPending ? "Creating..." : "Create Page"}
                  </Button>
                </div>
              </form>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
        <TabsList>
          <TabsTrigger value="pages">Pages</TabsTrigger>
          <TabsTrigger value="editor" disabled={!selectedPage}>Page Editor</TabsTrigger>
        </TabsList>

        <TabsContent value="pages" className="space-y-6">
          {/* Existing Pages Section */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Eye className="h-5 w-5" />
                Current Website Pages
              </CardTitle>
              <CardDescription>
                These are your current website pages. Import them to start editing with the page builder.
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
                {existingPages.map((page) => (
                  <Card key={page.slug} className="border-dashed border-2 hover:border-navy-blue/50 transition-colors">
                    <CardHeader>
                      <CardTitle className="text-sm font-medium text-navy-blue">{page.title}</CardTitle>
                      <CardDescription className="text-xs">/{page.slug}</CardDescription>
                    </CardHeader>
                    <CardContent>
                      <p className="text-sm text-charcoal mb-3">{page.description}</p>
                      <div className="flex gap-2">
                        <Button variant="outline" size="sm" className="text-xs">
                          <Eye className="w-3 h-3 mr-1" />
                          View Live
                        </Button>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            </CardContent>
          </Card>

          {/* Custom Pages Section */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Plus className="h-5 w-5" />
                Custom Pages
              </CardTitle>
              <CardDescription>
                Pages created through the page builder with custom content and components.
              </CardDescription>
            </CardHeader>
            <CardContent>
              {pages.length === 0 ? (
                <div className="text-center py-8 text-muted-foreground">
                  <p>No custom pages created yet. Create your first page using the "New Page" button above.</p>
                </div>
              ) : (
                <div className="grid gap-4">
                  {pages.map((page: Page) => (
              <Card key={page.id} className="hover:shadow-md transition-shadow">
                <CardHeader>
                  <div className="flex items-center justify-between">
                    <div>
                      <CardTitle className="text-navy-blue">{page.title}</CardTitle>
                      <CardDescription>/{page.slug}</CardDescription>
                    </div>
                    <div className="flex items-center gap-2">
                      <Badge className={getStatusBadgeColor(page.status)}>
                        {page.status}
                      </Badge>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => {
                          setSelectedPage(page);
                          setActiveTab("editor");
                        }}
                      >
                        <Edit className="w-4 h-4 mr-1" />
                        Edit
                      </Button>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => deletePageMutation.mutate(page.id)}
                      >
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    </div>
                  </div>
                </CardHeader>
                <CardContent>
                  <p className="text-sm text-charcoal">{page.description}</p>
                  <div className="mt-2 text-xs text-muted-foreground">
                    Created: {new Date(page.createdAt).toLocaleDateString()}
                  </div>
                </CardContent>
              </Card>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="editor" className="space-y-4">
          {selectedPage && (
            <div className="space-y-6">
              <div className="flex items-center justify-between">
                <div>
                  <h2 className="text-2xl font-bold text-navy-blue">{selectedPage.title}</h2>
                  <p className="text-charcoal">Editing page: /{selectedPage.slug}</p>
                </div>
                <div className="flex gap-2">
                  <Button variant="outline" size="sm">
                    <Eye className="w-4 h-4 mr-2" />
                    Preview
                  </Button>
                  <Button variant="outline" size="sm">
                    <Settings className="w-4 h-4 mr-2" />
                    Settings
                  </Button>
                </div>
              </div>

              <Card>
                <CardHeader>
                  <CardTitle className="text-navy-blue">Page Components</CardTitle>
                  <CardDescription>
                    Drag and drop components to build your page layout
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  {componentsLoading ? (
                    <div className="text-center py-8">Loading components...</div>
                  ) : pageComponents.length === 0 ? (
                    <div className="text-center py-8">
                      <div className="text-charcoal mb-4">No components added yet</div>
                      <Button
                        onClick={() => setShowCreateDialog(true)}
                        className="bg-navy-blue hover:bg-navy-blue/90"
                      >
                        <Plus className="w-4 h-4 mr-2" />
                        Add Component
                      </Button>
                    </div>
                  ) : (
                    <div className="space-y-4">
                      {pageComponents.map((component: PageComponent) => (
                        <Card key={component.id} className="border-l-4 border-l-navy-blue">
                          <CardHeader>
                            <div className="flex items-center justify-between">
                              <div>
                                <CardTitle className="text-sm font-medium">
                                  {component.name || component.type}
                                </CardTitle>
                                <CardDescription>
                                  Type: {component.type} • Order: {component.sortOrder}
                                </CardDescription>
                              </div>
                              <div className="flex gap-2">
                                <Button variant="outline" size="sm">
                                  <Edit className="w-4 h-4" />
                                </Button>
                                <Button variant="outline" size="sm">
                                  <Copy className="w-4 h-4" />
                                </Button>
                                <Button variant="outline" size="sm">
                                  <Trash2 className="w-4 h-4" />
                                </Button>
                              </div>
                            </div>
                          </CardHeader>
                          <CardContent>
                            <div className="text-sm text-charcoal">
                              <strong>Content:</strong> {JSON.stringify(component.content).substring(0, 100)}...
                            </div>
                          </CardContent>
                        </Card>
                      ))}
                    </div>
                  )}
                </CardContent>
              </Card>
            </div>
          )}
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/utils/brevo.ts.content:
// Brevo SDK utility functions

declare global {
  interface Window {
    Brevo: any[];
  }
}

export interface BrevoConfig {
  clientKey: string;
  formId?: string;
}

export const BREVO_CONFIG: BrevoConfig = {
  clientKey: import.meta.env.VITE_BREVO_CLIENT_KEY || '',
  formId: import.meta.env.VITE_BREVO_FORM_ID || '1'
};

export const showBrevoPopup = (formId?: string): void => {
  if (typeof window !== 'undefined' && window.Brevo) {
    window.Brevo.push(["showForm", {
      form_id: formId || BREVO_CONFIG.formId,
      delay: 0
    }]);
  } else {
    console.warn('Brevo SDK not loaded');
  }
};

export const initBrevoPopupTimer = (delayMs: number = 3000): void => {
  if (typeof window === 'undefined') return;

  const showPopupWithDelay = () => {
    setTimeout(() => {
      // Visit tracking with 30-minute session timeout
      const now = Date.now();
      const lastActivity = localStorage.getItem('brevo-last-activity');
      const sessionTimeout = 30 * 60 * 1000; // 30 minutes

      // Check if this is a new visit (first time or after 30min inactivity)
      let isNewVisit = false;
      if (!lastActivity || (now - parseInt(lastActivity)) > sessionTimeout) {
        isNewVisit = true;
        localStorage.setItem('brevo-visit-start', now.toString());
      }
      localStorage.setItem('brevo-last-activity', now.toString());

      // Popup frequency control - your exact settings
      const popupCount = parseInt(localStorage.getItem('brevo-popup-count') || '0');
      const lastPopupShown = localStorage.getItem('brevo-last-popup');
      const oneMinute = 60 * 1000; // 1 minute delay between popups
      const maxViews = 9; // Maximum 9 views per visitor
      const firstVisitDelay = 0; // Show immediately for first-time visitors

      let shouldShowPopup = false;

      // First time visitor - show immediately (0 delay after the 3-second page load delay)
      if (popupCount === 0) {
        shouldShowPopup = true;
      } 
      // Returning visitor - check limits and timing
      else if (popupCount < maxViews) {
        if (!lastPopupShown || (now - parseInt(lastPopupShown)) > oneMinute) {
          shouldShowPopup = true;
        }
      }

      if (shouldShowPopup) {
        showBrevoPopup();

        // Update tracking counters
        localStorage.setItem('brevo-popup-count', (popupCount + 1).toString());
        localStorage.setItem('brevo-last-popup', now.toString());

        console.log(`Brevo popup displayed - View #${popupCount + 1} of max ${maxViews}`);
      } else if (popupCount >= maxViews) {
        console.log('Brevo popup limit reached - maximum 9 views per visitor');
      } else {
        console.log('Brevo popup not shown - waiting for 1-minute delay');
      }
    }, delayMs);
  };

  if (document.readyState === 'loading') {
    window.addEventListener('load', showPopupWithDelay);
  } else {
    showPopupWithDelay();
  }

  // Track user activity to maintain 30-minute session
  let activityTimer: NodeJS.Timeout;
  const trackActivity = () => {
    clearTimeout(activityTimer);
    activityTimer = setTimeout(() => {
      localStorage.setItem('brevo-last-activity', Date.now().toString());
    }, 1000);
  };

  // Listen for user activity to maintain session
  ['click', 'scroll', 'keypress', 'mousemove'].forEach(event => {
    document.addEventListener(event, trackActivity, { passive: true });
  });
};

export const resetBrevoPopupTimer = (): void => {
  if (typeof window !== 'undefined') {
    localStorage.removeItem('brevo-popup-count');
    localStorage.removeItem('brevo-last-popup');
    localStorage.removeItem('brevo-last-activity');
    localStorage.removeItem('brevo-visit-start');
    console.log('Brevo popup tracking reset - all counters cleared');
  }
};

// Check if Brevo SDK is loaded
export const isBrevoReady = (): boolean => {
  return typeof window !== 'undefined' && Array.isArray(window.Brevo);
};

